import React from "react";
import IMessage from "../alias/IMessage";
import { HashLink as Link } from "react-router-hash-link";

interface IProps {
  message: IMessage;
}

const ReplyMessageWrapper: React.FC<IProps> = ({ message }) => {
  if (!message.isReplyMessage) return <></>;
  let treeDots = "";
  if (message.replyedMessage && message.replyedMessage.length > 50)
    treeDots = "...";

  return (
    <Link smooth to={`#${message?.replyedMessageId}`}>
      <div
        style={{ color: "#343a40", marginBottom: "5px" }}
        className={`bg-light pt-1 pb-1 pr-3 pl-3 border-radius`}
      >
        <div>
          <strong>{message.replyedMessageTo}</strong>
        </div>
        <div>
          <small className="text-muted">
            {message.replyedMessage?.slice(0, 50) + treeDots}
          </small>
        </div>
      </div>
    </Link>
  );
};

export default ReplyMessageWrapper;
