import {
  Invitation,
  Inviter,
  RegistererState,
  Session,
  SessionState,
} from "sip.js";
import SettingsBusiness from "./business/Settings";
import UserBusiness from "./business/User";
import { Events, SubscriptionCallBack } from "./voice/types/types";
import SIP from "./voice/voicecommunicator";
export interface UpdateConstraintsParams {
  useVideo?: boolean;
  videoId?: string;
  audioId?: string;
  speakerId?: string;
}
export default interface SquadVoiceCommunicator {
  sip: SIP;
  nextSubscriptionId?: number;
  subscriptions?: Map<number, SubscriptionCallBack>;
}
export default class SquadVoiceCommunicator {
  constructor(
    private settings: SettingsBusiness,
    private user: UserBusiness,
    cb: SubscriptionCallBack,
    private constraints: UpdateConstraintsParams = {}
  ) {
    this.nextSubscriptionId = 0;
    this.subscriptions = new Map<number, SubscriptionCallBack>();
    this.makeSIP();
  }
  makeSIP() {
    this.settings.get((settings) => {
      this.user.get((user) => {
        this.sip = new SIP({
          user: settings.sipUser,
          password: settings.sipPw,
          domain: settings.sipDomain,
          displayName: user.name,
          wsURL: settings.webrtcUrl2 || `wss://${settings.sipDomain}:7443`,
          connectionCB: this.connectionListener.bind(this),
          onMakeCall: this.onMakeCall.bind(this),
          onReceiveCall: this.onReceiveCall.bind(this),
        });
      });
    });
  }
  getConstraints() {
    const newConstraints: MediaStreamConstraints = {
      video:
        this.constraints.useVideo && this.constraints.videoId
          ? {
              deviceId: this.constraints.videoId,
            }
          : this.constraints.useVideo || false,
      audio: this.constraints.audioId
        ? {
            deviceId: this.constraints.audioId,
          }
        : true,
    };
    return newConstraints;
  }
  getSpeakerId() {
    return this.constraints.speakerId;
  }
  updateConstraintsParams(params: UpdateConstraintsParams) {
    this.constraints = { ...this.constraints, ...params };
  }
  connectionListener(data: RegistererState) {
    switch (data) {
      case RegistererState.Initial:
        break;
      case RegistererState.Registered:
        this.notify(Events.CONNECTED);
        break;
      case RegistererState.Terminated:
        this.notify(Events.DISCONNECTED);
        break;
      case RegistererState.Unregistered:
        this.notify(Events.UNREGISTERED);
        break;
    }
  }
  makeCall(
    number: string,
    constraints: UpdateConstraintsParams = this.constraints
  ) {
    this.updateConstraintsParams(constraints);
    this.sip.invite(number, this.getConstraints());
  }
  onMakeCall = (state: SessionState, inviter: Inviter) => {
    console.log(`Session state changed to ${state}`);
    switch (state) {
      case SessionState.Initial:
        this.notify(Events.MAKE_CALL, inviter);
        break;
      case SessionState.Establishing:
        break;
      case SessionState.Established:
        const { receivingVideo, tagId } =
          this.sip.setupRemoteMedia(inviter, this.getSpeakerId()) || {};
        this.notify(Events.CALL_ON_GOING, { inviter, receivingVideo, tagId });
        break;
      case SessionState.Terminating:
      // fall through
      case SessionState.Terminated:
        this.notify(Events.CALL_HANGUP, inviter);
        this.sip.activeCalls.delete(inviter.id);
        this.sip.cleanupMedia(inviter.id);
        break;
      default:
        throw new Error("Unknown session state.");
    }
  };
  acceptCall = (call: Session, useVideo: boolean) => {
    const acceptedCall = { callId: call.id, useVideo };
    this.updateConstraintsParams({ useVideo });
    (call as Invitation).accept({
      sessionDescriptionHandlerOptions: {
        constraints: this.getConstraints(),
      },
    });
    this.notify(Events.ACCEPTED_CALL, acceptedCall);
  };
  onReceiveCall = (state: SessionState, invitation: Invitation) => {
    console.log(`Session state changed to ${state}`);
    switch (state) {
      case SessionState.Initial:
        this.notify(Events.RECEIVED_CALL, invitation);
        break;
      case SessionState.Establishing:
        break;
      case SessionState.Established:
        const { receivingVideo, tagId } =
          this.sip.setupRemoteMedia(invitation, this.getSpeakerId()) || {};
        this.notify(Events.CALL_ON_GOING, {
          invitation,
          receivingVideo,
          tagId,
        });
        break;
      case SessionState.Terminating:
      // fall through
      case SessionState.Terminated:
        this.notify(Events.CALL_HANGUP, invitation);
        this.sip.activeCalls.delete(invitation.id);
        this.sip.cleanupMedia(invitation.id);
        break;
      default:
        break;
    }
  };

  muteMic = (callId: string) => {
    this.notify(Events.MUTE_MIC, { callId, status: true });
    this.sip.muteMic(callId);
  };
  unMuteMic = (callId: string) => {
    this.notify(Events.MUTE_MIC, { callId, status: false });
    this.sip.unMuteMic(callId);
  };
  disableCam = (callId: string) => {
    this.notify(Events.MUTE_CAM, { callId, status: true });
    this.sip.disableCam(callId);
  };
  enableCam = (callId: string) => {
    this.notify(Events.MUTE_CAM, { callId, status: false });
    this.sip.enableCam(callId);
  };
  holdCall = (callId: string) => {
    this.notify(Events.HOLD_CALL, { callId, status: true });
    this.sip.holdCall(callId);
  };
  unHoldCall = (callId: string) => {
    Array.from(this.sip.activeCalls.values())
      .filter((cl) => cl.id !== callId)
      .forEach((call) => {
        this.sip.holdCall(call.id);
        this.notify(Events.HOLD_CALL, { callId: call.id, status: true });
      });
    this.notify(Events.HOLD_CALL, { callId, status: false });
    this.sip.unHoldCall(callId);
  };

  // < -- Observer Pattern https://refactoring.guru/pt-br/design-patterns/observer
  subscribe(subscribeCallback: SubscriptionCallBack) {
    this.subscriptions?.set(this.nextSubscriptionId || 0, subscribeCallback);
    if (this.nextSubscriptionId) this.nextSubscriptionId += 1;
  }
  removeSubscription(id: number) {
    this.subscriptions?.delete(id);
  }
  removeAllSubscription() {
    this.subscriptions = new Map<number, SubscriptionCallBack>();
  }
  notify(event: Events, data: any = null) {
    if (this.subscriptions) {
      this.subscriptions.forEach((subscribeCallback) => {
        if (subscribeCallback) subscribeCallback(event, data);
      });
    }
  }
  // -- >
}
