import API from "@digivox/apicommunicator";
import IGroup from "../../alias/IGroup";
import INewGroup from "../../alias/INewGroup";
import IMember from "../../alias/IMember";
import IContact from "../../alias/IContact";
import { parseGroup } from "../utils/parseUtils";
import ContactsBusiness from "./Contacts";
import SettingsBusiness from "./Settings";
import {
  LeaveGroupCallback,
  LeaveGroupRequest,
  RemoveMemberCallback,
  AddMemberCallback,
  ChangeGroupInfoRequest,
  ChangeGroupInfoCallback
} from "@digivox/apicommunicator/dist/types/types/types";

export interface NewGroupCallback {
  (group: IGroup | undefined): void;
}
export interface GroupsCallBack {
  (groups: IGroup[]): void;
}
export default interface GroupsBusiness {
  getGroups(): Map<string, IGroup>;
  list(callback: GroupsCallBack): void;
  create(group: INewGroup, cb: NewGroupCallback): void;
}
export default class GroupsBusiness {
  public groups: Map<string, IGroup>;
  constructor(
    private api: API,
    private settings: SettingsBusiness,
    private contacts: ContactsBusiness
  ) {
    this.groups = new Map<string, IGroup>();
  }
  getGroups = () => {
    return this.groups;
  };
  list = (callback: GroupsCallBack) => {
    this.api.getGroups(
      { paramsUrl: { members: true, limit: 5000 } },
      (apiGroups: any, err) => {
        // Populando o Map de grupos
        if (!err) {
          if (!this.api) return;
          this.api.getContacts(
            { paramsUrl: { contactType: "group", limit: 5000, added: true } },
            (apiGroupsContactsFormat) => {
              this.populate(apiGroups, apiGroupsContactsFormat);
              callback(Array.from(this.groups.values()));
            }
          );
        }
      }
    );
  };
  create = (group: INewGroup, callback: NewGroupCallback) => {
    if (!this.api) return;
    this.settings.get((settings) => {
      const contactsId = group.contacts.map((contact) => contact.id);
      const body = {
        name: group.name,
        description: group.description,
        members: contactsId,
        domain: settings.sipDomain,
      };
      this.api.createGroup({ body }, (returnGroup, err) => {
        if (err) return;
        if (!this.api) return;
        if (!returnGroup) return;
        this.api.getGroup(
          returnGroup.id,
          { paramsUrl: { members: true } },
          (groupWithMember) => {
            const parsedGroup = parseGroup(groupWithMember);
            this.groups.set(parsedGroup.groupId, parsedGroup);
            callback(parsedGroup);
          }
        );
      });
    });
  };
  populate(apiGroups: any, apiGroupsContactsFormat: any) {
    apiGroups.groups.forEach((group: any) => {
      const apiContactGroup = apiGroupsContactsFormat.contacts.find(
        (grp: any) => grp.id === group.id
      );
      this.groups.set(
        group.id,
        parseGroup(Object.assign(group, apiContactGroup))
      );
    });
  };
  leftingGroup(request: LeaveGroupRequest, callback: LeaveGroupCallback){
    this.api.leaveGroup({memberId: request.memberId, groupId: request.groupId}, callback);
  };
  removeMember = (grp: string, mbr: IMember, callback: RemoveMemberCallback) => {
    this.api.removeMember(
      {
        groupId: grp,
        memberId: mbr.userId
      },
      callback
    );
  };
  editGroupInfo(request: ChangeGroupInfoRequest, callback: ChangeGroupInfoCallback){
    this.api.changeGroupInfo({
      groupId: request.groupId,
      groupName: request.groupName,
      members: request.members,
      description: request.description
    }, callback)
  }
  addMember = (grp: IGroup, ctc: IContact, callback: AddMemberCallback) => {
    this.api.addMember(
      {
        groupId: grp.groupId,
        memberId: ctc.id
      },
      callback
    ) 
  }
};
