import React, { createContext, useContext, useState } from "react";
import IChat from "../alias/IChat";
import { IConference } from "../alias/IConference";
import ICreateConference from "../alias/ICreateConference";
import ChatType from "../enuns/ChatType";
import { useChat } from "./ChatContext";

interface IConferenceContextData {
  createConference: ICreateConference | undefined;
  setCreateConference(value: ICreateConference | undefined): void;
  listRooms: boolean;
  setListRooms(val: boolean): void;
  conferenceList: IConference[];
  setConferenceList(list: IConference[]): void;
  openConference(value: IConference): void;
}

export const ConferenceContext = createContext<IConferenceContextData>(
  {} as IConferenceContextData
);

export const ConferenceProvider: React.FC = ({ children }) => {
  const [listRooms, setListRooms] = useState<boolean>(false);
  const [conferenceList, setConferenceList] = useState<IConference[]>([]);
  const [createConference, setCreateConference] = useState<ICreateConference>();
  const { activeChat, chats, setChats, setActiveChat, setShowChat } = useChat();

  const openConference = (obj: IConference) => {
    if (activeChat?.jid === obj.jid) return;
    let find = false;

    setChats(
      chats.map((item) => {
        if (item.jid === obj.jid) {
          item.unRead = 0;
          item.mencioned = 0;
          item.messageLoaded = item.messages.length >= 20 ? false : true;
          setShowChat(true);
          setActiveChat(item);
          find = true;
        }
        return item;
      })
    );

    if (!find) {
      let newChat: IChat = {
        id: obj.jid.split("@")[0],
        jid: obj.jid,
        name: obj.name,
        messageLoaded: true,
        unRead: 0,
        chatType: ChatType.CONFERENCE,
        inputMessage: "",
        isTyping: false,
        messages: [],
        favorite: false,
        added: true,
        conferenceRoomParticipants: obj.conferenceRoomParticipants,
      };
      setShowChat(true);
      setActiveChat(newChat);
    }
  };

  return (
    <ConferenceContext.Provider
      value={{
        createConference,
        setCreateConference,
        conferenceList,
        setConferenceList,
        listRooms,
        setListRooms,
        openConference,
      }}
    >
      {children}
    </ConferenceContext.Provider>
  );
};

export function useConference() {
  return useContext(ConferenceContext);
}
