import IContact from "../alias/IContact";
import IContactList from "../alias/IContactList";

const sortContactGroup = (contacts: IContact[]) => {
  let list: IContactList[] = [];

  // cria os grupos das letras
  contacts.forEach((contact) => {
    let letter = contact.name.substring(0, 1);
    let foundLetter = false;

    list.forEach((li) => {
      if (li.group === letter && !foundLetter) {
        foundLetter = true;
      }
    });

    if (!foundLetter) {
      list.push({ group: letter, children: [] });
    }
  });

  // adiciona os contatos nas suas respectivas letras
  contacts.forEach((contact) => {
    let letter = contact.name.substring(0, 1);

    list = list.map((li) => {
      if (li.group === letter) {
        li.children.push(contact);
      }
      return li;
    });
  });

  return list.sort(function (a: IContactList, b: IContactList) {
    var nameA = a.group.toUpperCase(); // ignore upper and lowercase
    var nameB = b.group.toUpperCase(); // ignore upper and lowercase
    if (nameA < nameB) {
      return -1;
    }
    if (nameA > nameB) {
      return 1;
    }
    return 0;
  });
};

export default sortContactGroup;
