import React from 'react';
import { Button } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import useCopy from "@react-hook/copy";
import { useTranslation } from 'react-i18next';

export interface ICopyModeratorLinkButtonInActiveCall{
    moderatorLink: string,
    buttonIcon: string
}

const CopyModeratorLinkButtonInActiveCall : React.FC<ICopyModeratorLinkButtonInActiveCall> = ({moderatorLink, buttonIcon}) => {
    const { copy } = useCopy(moderatorLink);
    const { t } = useTranslation();
    return(
        <>
            <ToastContainer/>
            <Button
            type="button"
            color="none"
            onClick={() => {
            copy();
            toast.success(t('Copied to clipboard a participant invite!'), {
            position: "top-center",
            autoClose: 1700,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            progress: undefined,
            });
            }}
            className="nav-btn user-profile-show"
            >
            <i className={buttonIcon}></i>
            </Button>
        </>
    )
}

export default CopyModeratorLinkButtonInActiveCall;
