import React, { useState } from 'react';
import { Nav, NavItem, NavLink, UncontrolledTooltip, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import avatar1 from "./../assets/images/users/avatar-1.jpg";
import ScreenType from "../enuns/ScreenType";
import { useMainBox } from '../contexts/MainBoxContext';
import { useLayout } from '../contexts/LayoutContext';
const { setActiveTab } = useLayout();
const [dropdownOpen, setDropdownOpen] = useState(false);
const [dropdownOpenMobile, setDropdownOpenMobile] = useState(false);
const toggle = () => setDropdownOpen(!dropdownOpen);
const toggleMobile = () => setDropdownOpenMobile(!dropdownOpenMobile);
const toggleTab = (tab: string) => {
  setActiveTab(tab);
};

const NavHorizontal = () => {
    return(
        <div className="side-menu-horizontal flex-lg-column">
            <div className="navbar-brand-box">
        </div>
        <div className="flex-lg-column my-auto">
        <Nav
            pills
            className="side-menu-nav-new justify-content-center"
            role="tablist"
            >
            
            <NavItem id="PaginaInicial">
            <NavLink
                onClick={() => {
                setScreen(ScreenType.PAGINAINICIAL);
                }}
            >
                <i className="ri-home-3-line"></i>
            </NavLink>
            </NavItem>
            <UncontrolledTooltip target="PaginaInicial" placement="top">
            Página inicial
            </UncontrolledTooltip>

            <NavItem id="FornecedoresFavoritos">
            <NavLink
                onClick={() => {
                setScreen(ScreenType.FORNECEDORESFAVORITOS);
                }}
            >
                <i className="ri-star-line"></i>
            </NavLink>
            </NavItem>
            <UncontrolledTooltip target="FornecedoresFavoritos" placement="top">
            Fornecedores favoritos
            </UncontrolledTooltip>


            <NavItem id="MinhasCotacoes">
            <NavLink
                onClick={() => {
                setScreen(ScreenType.MINHASCOTACOES);
                }}
            >
                <i className="ri-file-list-line"></i>
            </NavLink>
            </NavItem>
            <UncontrolledTooltip target="MinhasCotacoes" placement="top">
            Minhas cotações
            </UncontrolledTooltip>

            <Dropdown
            nav
            isOpen={dropdownOpenMobile}
            toggle={toggleMobile}
            className="profile-user-dropdown d-inline-block d-lg-none"
            >
            <DropdownToggle nav>
                <img
                src={avatar1}
                alt="chatvia"
                className="profile-user rounded-circle"
                />
            </DropdownToggle>
            <DropdownMenu>
                <DropdownItem
                onClick={() => {
                    toggleTab("profile");
                }}
                >
                Profile
                <i className="ri-profile-line float-right text-muted"></i>
                </DropdownItem>
                <DropdownItem
                onClick={() => {
                    toggleTab("settings");
                }}
                >
                Settings
                <i className="ri-settings-3-line float-right text-muted"></i>
                </DropdownItem>
                <DropdownItem divider />
                <DropdownItem>
                Logout
                <i className="ri-logout-circle-r-line float-right text-muted"></i>
                </DropdownItem>
            </DropdownMenu>
            </Dropdown>
        </Nav>
        </div>

        <div className="flex-lg-column d-none d-lg-block">
        <Nav className="side-menu-nav justify-content-center">
            <Dropdown
            nav
            isOpen={dropdownOpen}
            className="btn-group dropup profile-user-dropdown"
            toggle={toggle}
            >
            <DropdownToggle nav>
                <img
                src={avatar1}
                alt=""
                className="profile-user rounded-circle"
                />
            </DropdownToggle>
            <DropdownMenu>
                <DropdownItem
                onClick={() => {
                    toggleTab("profile");
                }}
                >
                <div
                    style={{
                    display: "flex",
                    flexDirection: "row",
                    alignItems: "center",
                    }}
                >
                    <i className="ri-profile-line mr-2 text-muted"></i>
                    Profile
                </div>
                </DropdownItem>
                <DropdownItem
                onClick={() => {
                    toggleTab("settings");
                }}
                >
                <div
                    style={{
                    display: "flex",
                    flexDirection: "row",
                    alignItems: "center",
                    }}
                >
                    <i className="ri-settings-3-line mr-2 text-muted"></i>
                    Settings
                </div>
                </DropdownItem>
                <DropdownItem divider />
                <DropdownItem>
                <div
                    style={{
                    display: "flex",
                    flexDirection: "row",
                    alignItems: "center",
                    }}
                >
                    <i className="ri-logout-circle-r-line mr-2 text-muted"></i>
                    Logout
                </div>
                </DropdownItem>
            </DropdownMenu>
            </Dropdown>
        </Nav>
        </div>
    </div>
    )
}

export default NavHorizontal;
