import API from "@digivox/apicommunicator";
import IContact from "../../alias/IContact";
import { parseContact } from "../utils/parseUtils";
import SettingsBusiness from "./Settings";

interface ContactsCallBack {
  (contacts: IContact[]): void;
}
export default interface ContactsBusiness {}
export default class ContactsBusiness {
  private contacts: Map<string, IContact>;
  constructor(private api: API, private settings: SettingsBusiness) {
    this.contacts = new Map<string, IContact>();
  }
  getContacts() {
    return this.contacts;
  }
  list(callback: ContactsCallBack) {
    this.settings.get((settings) => {
      this.api.getContacts(
        {
          paramsUrl: {
            added: true,
            limit: 5000,
            contactType: "contact",
            domain: settings.sipDomain,
          },
        },
        (apiContacts: any, err: any) => {
          // Populando o Map de contatos
          if (!err) {
            if (!this.api) return;
            this.populate(apiContacts);
            callback(Array.from(this.contacts.values()));
          }
        }
      );
    });
  }
  populate(apiContacts: any) {
    apiContacts.contacts.forEach((contact: any) => {
      this.contacts.set(contact.id, parseContact(contact));
    });
  }
}
