import React, { useState } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  CardBody,
  Button,
  ModalFooter,
  Input,
} from "reactstrap";

import { useModal } from "./../contexts/ModalContext";
import { useAuth } from "./../contexts/AuthContext";
const ModalEditInfo: React.FC = () => {
  function validateEmail(email: string) {
    const re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return re.test(String(email).toLowerCase());
  }
  
  const { showModalEditInfo, setShowModalEditInfo } = useModal();

  const { setChangedUserData, user } = useAuth();
  const [nome, setNome] = useState("");
  const [email, setEmail] = useState("");

  const toggle = () => setShowModalEditInfo(!showModalEditInfo);

  return (
    <Modal
      backdrop="static"
      isOpen={showModalEditInfo}
      centered
      toggle={toggle}
    >
      <ModalHeader toggle={toggle}>Edit personal info</ModalHeader>
      <ModalBody>
        <CardBody>
          <div>
            <div className="mt-0">Edit name:</div>
            <Input
              placeholder="Nome"
              defaultValue={user.name}
              onChange={(e) => setNome(e.target.value)
              }
            ></Input>
            <div className="mt-4">Edit e-mail:</div>
            <Input
              defaultValue={user.email}
              placeholder="E-mail"
              onChange={(e) => setEmail(e.target.value)}
            ></Input>
            <ModalFooter className="border-0">
              <Button type="submit" color="default" onClick={toggle}>
                Close
              </Button>
              {
                validateEmail(email) && <Button
                type="submit"
                color="primary"
                onClick={(e) => {
                  e.preventDefault();
                  toggle();
                  setChangedUserData({ name: nome, email: email });
                }}
              >
                Confirm
              </Button>
              }
              {
              <Button
                type="submit"
                color="primary"
                onClick={(e) => {
                  e.preventDefault();
                  toggle();
                  setChangedUserData({ name: nome, email: email });
                }}
                disabled
              >
                Confirm
              </Button>
              }
            </ModalFooter>
          </div>
        </CardBody>
      </ModalBody>
    </Modal>
  );
};

export default ModalEditInfo;
