import React, { useState } from "react";
import { Input, InputGroupAddon, InputGroup, Button, UncontrolledDropdown, DropdownToggle, DropdownItem, DropdownMenu } from "reactstrap";
import IChat from "../alias/IChat";
import { useChat } from "../contexts/ChatContext";
import { useTranslation } from "react-i18next";
// import FavoriteUsers from "./FavoriteUsers";
import ChatList from "./ChatList";
import DialPad from "./DialPad";
import { useModal } from "../contexts/ModalContext";

const ChatTab: React.FC = () => {
  const { chats } = useChat();
  const { showModalDialPad, setShowModalDialPad } = useModal();
  const [searchChat, setSearchChat] = useState("");
  const { t } = useTranslation();
  const searchArray = (chats: IChat[], search: string) => {
    return chats.filter((item) => {
      return item.name.toUpperCase().match(search.toUpperCase());
    });
  };
  const toggle = () => {
    setShowModalDialPad(!showModalDialPad);
  };
  return (
    <>
      <div>
        <div className="px-4 pt-4">
          <div style={{display: "flex", flexDirection: "row", alignItems: "flex-end", justifyContent: "flex-end"}}>
            <h4 className="mb-4">{t("Chats")}</h4>
            <UncontrolledDropdown>
              <DropdownToggle>
                <i className="ri-more-2-fill"></i>
              </DropdownToggle>
              <DropdownMenu>
                <DropdownItem>
                  Favorite messages
                  <i className="ri-star-fill"></i>
                </DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>
          </div>
          <div className="search-box chat-search-box">
            <InputGroup size="lg" className="mb-3 bg-light rounded-lg">
              <InputGroupAddon addonType="prepend">
                <Button
                  color="link"
                  className="text-muted pr-1 text-decoration-none"
                  type="button"
                >
                  <i className="ri-search-line search-icon font-size-18"></i>
                </Button>
              </InputGroupAddon>
              <Input
                type="text"
                value={searchChat}
                onChange={(e) => setSearchChat(e.target.value)}
                className="form-control bg-light"
                placeholder={t("Search messages or users")}
              />
              {/* <InputGroupAddon addonType="append">
                <Button
                  color="link"
                  className="text-muted pr-1 text-decoration-none"
                  type="button"
                  onClick={toggle}
                >
                  <i className="ri-grid-fill search-icon font-size-18"></i>
                </Button>
              </InputGroupAddon> */}
            </InputGroup>
          </div>
        </div>

        {/* <FavoriteUsers /> */}

        <div className="px-2">
          <h5 className="mb-3 px-3 font-size-16">{t("Recent")}</h5>
          <ChatList recentChatList={searchArray(chats, searchChat)} />
        </div>
        <DialPad show={showModalDialPad} toggle={toggle} />
      </div>
    </>
  );
};

export default ChatTab;
