import React from "react";
import IMessage from "../alias/IMessage";

interface IProps {
  message: IMessage;
}

const ChatMessagePhoto: React.FC<IProps> = ({ message }) => {
  if (message?.userProfilePicture) {
    return <img src={message.userProfilePicture} alt="chatvia" />;
  } else {
    return (
      <div className="chat-user-img align-self-center mr-3">
        <div className="avatar-xs">
          <span className="avatar-title rounded-circle bg-soft-primary text-primary">
            {message?.userName && message?.userName.trim().charAt(0)}
          </span>
        </div>
      </div>
    );
  }
};

export default ChatMessagePhoto;
