import React, {useState} from "react";
import Dropzone from "react-dropzone";
import { useAuth } from "../contexts/AuthContext";
import { useChat } from "../contexts/ChatContext";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";

interface IProps{
    addFile(value: any): any;
    files: any;
}

const { chats, setChats, activeChat } = useChat();
const { user } = useAuth();

const onAddMessage = (file: File) => {
    setChats(
      chats.map((item) => {
        if (activeChat)
          if (item.id === activeChat?.id) {
            item.messages.push({
              id: `${item.messages.length + 1}`,
              userName: user.name,
              message: "Carregando...",
              time: "",
              isImageMessage: false,
              isFileMessage: false,
              isVideoMessage: false,
              isAudioMessage: false,
              fileUrl: "",
              sendFile: file,
              toUser: activeChat.id,
              fromUser: user.id,
              deliverStatus: DeliverStatus.SENDING,
              readStatus: ReadStatus.UNREAD,
              deliverTimestamp: "",
              readTimestamp: "",
              content: "metadata",
              contentType: "type",
              isReplyMessage: false,
              isForwardMessage: false,
            });
          }
        return item;
      })
    );
  };

const ImageUpload: React.FC<IProps> = ({ addFile, files }) => {
  const [warningMsg, setWarningMsg] = useState("");
  const onDrop = (accepted: any, rejected: any) => {
    if (Object.keys(rejected).length !== 0) {
      const message = "Please submit valid file type";
      setWarningMsg(message);
    } else {
      addFile(accepted);
      setWarningMsg("");
      console.log(accepted[0].preview);

      var blobPromise = new Promise((resolve, reject) => {
        const reader = new window.FileReader();
        reader.readAsDataURL(accepted[0]);
        reader.onloadend = () => {
          const base64data = reader.result;
          resolve(base64data);
        };
      });
      blobPromise.then(value => {
        // console.log(value);
      });
      //________________________________________________________________
        let file = files?.item(0);
        if (file && file.size > 15728640) {
          alert(
            "Tamanho de arquivo " +
              file.name +
              " não suportado. Por favor, envie um arquivo menor do que 15mb"
          );
          return;
        }else{
          onAddMessage(file);
        }
    }
  };

    const thumbs = 
    <img style={{
    width: "150px",
    height: "150px",
    borderRadius: "50%",
    objectFit: "cover",
    objectPosition: "center"}} 
    src={files} alt="profile" />;

    const render =
      Object.keys(files).length !== 0 ? (
        <aside>{thumbs}</aside>
      ) : (
        <p className="hello">+ image here to prompt users to click</p>
      );

    return (
      <div
      style={{
        width: "150px",
        height: "150px",
        borderRadius: "50%",
        objectFit: "cover",
        objectPosition: "center",
        border: " 1px dashed"
      }}>
        <p>{warningMsg}</p>

        <Dropzone
          multiple={false}
          /* accept="image/*" */
          onDrop={(accepted, rejected) => onDrop(accepted, rejected)}
        >
          {({getRootProps, getInputProps}) => (
            <div {...getRootProps()}>
            <input {...getInputProps()} />
            {render}
            </div>
        )}
        </Dropzone>
      </div>
    );
  }

export default ImageUpload;
