import React, { useEffect, useState } from "react";
// import Webcam from "react-user-media";
import { useTranslation } from "react-i18next";
import { useModal } from "../contexts/ModalContext";
import { Video } from "./Video";
import {
  Button,
  Input,
  InputGroupText,
  InputGroup,
  InputGroupAddon,
  Modal,
  Progress,
  ModalBody,
  ModalFooter,
  ModalHeader,
} from "reactstrap";
import { useCall } from "../contexts/CallContext";

const ModalConfigDevices: React.FC = () => {
  const [selectedVideo, setSelectedVideo] = useState<MediaDeviceInfo>();
  const [selectedAudio, setSelectedAudio] = useState<MediaDeviceInfo>();
  const [selectedSpeaker, setSelectedSpeaker] = useState<MediaDeviceInfo>();
  const [videoDevices, setVideoDevices] = useState<MediaDeviceInfo[]>([]);
  const [audioDevices, setAudioDevices] = useState<MediaDeviceInfo[]>([]);
  const [speakerDevices, setSpeakerDevices] = useState<MediaDeviceInfo[]>([]);
  const [speakerVolume, setSpeakerVolume] = useState<number>();
  const [speaking, setSpeaking] = useState<boolean>();
  const [stream, setStream] = useState<MediaStream | undefined>();
  const { setUpdateConstraints } = useCall();
  const { showModalDevicesConfig, setShowModalDevicesConfig } = useModal();
  const { t } = useTranslation();
  const init = () => {
    navigator.mediaDevices.enumerateDevices().then((devices) => {
      const vidDevices: MediaDeviceInfo[] = [];
      const audDevices: MediaDeviceInfo[] = [];
      const spkDevices: MediaDeviceInfo[] = [];
      devices.forEach((device) => {
        switch (device.kind) {
          case "audioinput":
            audDevices.push(device);
            break;
          case "videoinput":
            vidDevices.push(device);
            break;
          case "audiooutput":
            spkDevices.push(device);
            break;
        }
      });
      setAudioDevices(audDevices);
      setVideoDevices(vidDevices);
      setSpeakerDevices(spkDevices);
    });
  };

  const handleChangeVideo = (ev: React.ChangeEvent<HTMLInputElement>) => {
    const idVideo = ev.target.value;
    const video = videoDevices.filter((v) => v.deviceId === idVideo)[0];
    setSelectedVideo(video);
  };
  const handleChangeAudio = (ev: React.ChangeEvent<HTMLInputElement>) => {
    const idAudio = ev.target.value;
    const audio = audioDevices.filter((v) => v.deviceId === idAudio)[0];
    setSelectedAudio(audio);
  };
  const handleChangeSpeaker = (ev: React.ChangeEvent<HTMLInputElement>) => {
    const idSpeaker = ev.target.value;
    const speaker = speakerDevices.filter((v) => v.deviceId === idSpeaker)[0];
    setSelectedSpeaker(speaker);
  };
  const parseAndSetSpeakerVolumn = (volume: number) => {
    setSpeakerVolume(Math.round(Math.abs(volume < -100 ? -100 : volume)));
  };
  const changeSpeaking = (spk: boolean) => {
    setSpeaking(spk);
  };
  const onPreferencesSubmit = () => {
    setUpdateConstraints({
      videoId: selectedVideo?.deviceId,
      audioId: selectedAudio?.deviceId,
      speakerId: selectedSpeaker?.deviceId,
    });
    setShowModalDevicesConfig(false);
  };
  useEffect(init, []);
  return (
    <Modal centered isOpen={showModalDevicesConfig}>
      <ModalHeader>{t("Config Devices")}</ModalHeader>
      <ModalBody>
        <div className="ct-live-video-view mb-2 border">
          {selectedVideo ? (
            <Video
              audioId={selectedAudio ? selectedAudio.deviceId : undefined}
              videoId={selectedVideo ? selectedVideo.deviceId : undefined}
              speakerId={selectedSpeaker ? selectedSpeaker.deviceId : undefined}
              stream={stream}
              setStream={setStream}
              speakerVolume={speakerVolume}
              speaking={speaking}
              modalOpen={showModalDevicesConfig}
              setSpeakerVolume={parseAndSetSpeakerVolumn}
              setSpeaking={changeSpeaking}
            />
          ) : (
            <div className="w-100 h-100 d-flex justify-content-center align-items-center">
              No data
            </div>
          )}
          {/* <Webcam ref={(ref) => this.webcam = ref} 
					 	video={{deviceId: {exact: selectedVideo ? selectedVideo.id : undefined}}}
						audio={{deviceId: {exact: selectedAudio ? selectedAudio.id : undefined}}}
						/>  */}
        </div>
        <div className="ct-video-controller">
          <InputGroup className="mb-2">
            <InputGroupAddon addonType="prepend">
              <InputGroupText>
                <i className="ri-vidicon-line" />
              </InputGroupText>
            </InputGroupAddon>
            <Input
              type="select"
              defaultValue={
                selectedVideo && selectedVideo.deviceId
                  ? selectedVideo.deviceId
                  : "default"
              }
              onChange={handleChangeVideo}
            >
              {videoDevices.map((video, index) => (
                <option key={index} value={video.deviceId}>
                  {video.label}
                </option>
              ))}
            </Input>
          </InputGroup>
          <InputGroup className="mb-2">
            <InputGroupAddon addonType="prepend">
              <InputGroupText>
                <i className="ri-mic-line" />
              </InputGroupText>
            </InputGroupAddon>
            <Input
              type="select"
              defaultValue={
                selectedAudio && selectedAudio.deviceId
                  ? selectedAudio.deviceId
                  : "default"
              }
              onChange={handleChangeAudio}
            >
              {audioDevices.map((audio, index) => (
                <option key={index} value={audio.deviceId}>
                  {audio.label}
                </option>
              ))}
            </Input>
          </InputGroup>
          <InputGroup className="mb-2">
            <InputGroupAddon addonType="prepend">
              <InputGroupText>
                <i className="ri-speaker-line" />
              </InputGroupText>
            </InputGroupAddon>
            <Input
              type="select"
              defaultValue={
                selectedSpeaker && selectedSpeaker.deviceId
                  ? selectedSpeaker.deviceId
                  : "default"
              }
              onChange={handleChangeSpeaker}
            >
              {speakerDevices.map((speaker, index) => (
                <option key={index} value={speaker.deviceId}>
                  {speaker.label}
                </option>
              ))}
            </Input>
          </InputGroup>
          <Progress
            color="info"
            className="mb-2"
            value={speaking ? speakerVolume : 0}
          />
        </div>
      </ModalBody>
      <ModalFooter>
        <Button type="button" className="mr-2" onClick={onPreferencesSubmit}>
          {t("Salvar")}
        </Button>
        <Button onClick={() => setShowModalDevicesConfig(false)}>
          {" "}
          {t("Cancel")}
        </Button>
      </ModalFooter>
    </Modal>
  );
};
export default ModalConfigDevices;
