import React from "react";
import { useCall } from "../contexts/CallContext";
import { useChat } from "../contexts/ChatContext";
import CallStatus from "../enuns/CallStatus";
import ChatBody from "./ChatBody";
import ChatHead from "./ChatHead";
import ChatHeadConference from "./ChatHeadConference";
import ChatInput from "./ChatInput";
import ChatInputAnswer from "./ChatInputAnswer";
import ChatProfileSidebar from "./ChatProfileSidebar";
import ModalForwardMessageTo from "./ModalForwardMessageTo";

const ChatBox = () => {
  const { showChatProfileSidebar, setShowChatProfileSidebar } = useChat();
  const { activeChat } = useChat();
  const { currentCalls } = useCall();
  const currentCall = Array.from(currentCalls.values()).find(
    (call) => call.callerObject?.jid === activeChat.jid
  );
  return (
    <>
      <div className={`user-chat w-100 user-chat-show`}>
        <div className="d-lg-flex">
          <div className={showChatProfileSidebar ? "w-70" : "w-100"}>
            {/* render user head */}
            {currentCall?.callStatus === CallStatus.ON_GOING ||
            currentCall?.callStatus === CallStatus.CONNECTING ||
            currentCall?.callStatus === CallStatus.CALLING ? (
              <ChatHeadConference
                chatProfileSidebarOpen={showChatProfileSidebar}
                setChatProfileSidebarOpen={setShowChatProfileSidebar}
              />
            ) : (
              <ChatHead
                chatProfileSidebarOpen={showChatProfileSidebar}
                setChatProfileSidebarOpen={setShowChatProfileSidebar}
              />
            )}
            <ChatBody />
            <ModalForwardMessageTo />
            <ChatInputAnswer showChatProfileSidebar={showChatProfileSidebar} />
            <ChatInput />
          </div>

          <ChatProfileSidebar
            show={showChatProfileSidebar}
            setShow={setShowChatProfileSidebar}
          />
        </div>
      </div>
    </>
  );
};

export default ChatBox;
