import React, { useState } from "react";
import { Input, Label } from "reactstrap";
import IContact from "../alias/IContact";
import { GroupProvider, useGroup } from "../contexts/GroupContext";
import { useChat } from "../contexts/ChatContext";
import { useContact } from "../contexts/ContactContext";
import { SearchContact } from "../helpers/SearchContact";
import sortContactGroup from "../helpers/SortContactGroup";
import IGroup from "../alias/IGroup";

interface IProps {
  search?: string;
  handleCheckContact(selected: boolean, contact: IContact): void;
}

const SelectMember: React.FC<IProps> = ({ search, handleCheckContact }) => {
  const {
    contactsOutsideGroup,
    setContactsOutsideGroup,
    checkedMembers,
    setCheckedMembers,
    contacts
  } = useContact();
  const { groups } = useGroup();
  const { activeChat } = useChat();
  
  let actualGroup = groups.find((grp) => (
    grp.groupId === activeChat.groupId
  ));  

  let actualGroupMembersId = actualGroup?.members?.map((mbrs) => {
    return mbrs.userId;
  });

  let [contactStorage, setContactStorage] = useState<IContact[]>([]);

  contacts.map((ctc) =>
      actualGroupMembersId?.forEach((str) => {
        if(str !== ctc.id){
          setContactStorage([...contactStorage, ctc])
        }
      })
  )
  


  return (
    <div className="mt-2">
      {sortContactGroup(
        SearchContact(contactStorage, search ? search : "")
      ).map((contact, key) => (
        <div key={key}>
          <div className="ml-3 pl-1 font-weight-bold text-primary">
            {contact.group}
          </div>

          <ul className="list-unstyled contact-list mb-2">
            {contact.children.map((child, keyChild) => (
              <li key={keyChild}>
                <div className="custom-control custom-checkbox">
                  <button onClick={() => setContactStorage([])}>Limpar</button>
                  <Input
                    type="checkbox"
                    className="custom-control-input"
                    onChange={(e) => {
                      handleCheckContact(e.target.checked, child);
                      setCheckedMembers([...checkedMembers, child]);
                    }}
                    id={"memberCheck" + child.id}
                    value={child.name}
                  />
                  <Label
                    className="custom-control-label"
                    htmlFor={"memberCheck" + child.id}
                  >
                    {child.name}
                  </Label>
                </div>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default SelectMember;
