import React from "react";

import IChat from "../alias/IChat";
import IMessage from "../alias/IMessage";

import { useAuth } from "../contexts/AuthContext";

import ChatMessagePhoto from "./ChatMessagePhoto";
import ChatMessageFooter from "./ChatMessageFooter";
import ChatMessageText from "./ChatMessageText";
import ChatMessageMenuOption from "./ChatMessageMenuOption";
import ReplyMessageWrapper from "./ReplyMessageWrapper";

interface IProps {
  activeChat: IChat;
  message: IMessage;
}

const ChatMessage: React.FC<IProps> = ({ activeChat, message }) => {
  const { user } = useAuth();

  return (
    <li
      className={user.id === message.fromUser ? "right" : ""}
      id={`${message.id}`}
    >
      <div className="conversation-list">
        <div className="chat-avatar">
          <ChatMessagePhoto message={message} />
        </div>

        <div
          className={
            "user-chat-content " +
            (user.id === message.fromUser
              ? "message-chat-left"
              : "message-chat-right")
          }
        >
          <div className="ctext-wrap">
            <div className="ctext-wrap-content">
              <ReplyMessageWrapper message={message} />
              <ChatMessageText message={message} />
              <ChatMessageFooter message={message} user={user} />
            </div>
            <ChatMessageMenuOption activeChat={activeChat} message={message} />
          </div>
          <div className="conversation-name">{message.userName}</div>
        </div>
      </div>
    </li>
  );
};

export default ChatMessage;
