import React, { useState } from "react";
import {
  Dropdown,
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  Media,
  Button,
  Row,
  Col,
} from "reactstrap";
import { Link } from "react-router-dom";
import { useChat } from "../contexts/ChatContext";

interface IProps {
  chatProfileSidebarOpen: boolean;
  setChatProfileSidebarOpen(value: boolean): void;
}

const ChatRightHead: React.FC<IProps> = ({
  chatProfileSidebarOpen,
  setChatProfileSidebarOpen,
}) => {
  const { activeChat } = useChat();

  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggle = () => setDropdownOpen(!dropdownOpen);

  const ChatProfileSidebar = () => {
    setChatProfileSidebarOpen(!chatProfileSidebarOpen);
  };

  function closeUserChat(e: any) {
    e.preventDefault();
    var userChat = document.getElementsByClassName("user-chat");
    if (userChat) {
      userChat[0].classList.remove("user-chat-show");
    }
  }

  function deleteMessage(e: any) {}

  return (
    <React.Fragment>
      <div className="p-3 p-lg-4 border-bottom">
        <Row className="align-items-center">
          <Col sm={4} xs={8}>
            <Media className="align-items-center">
              <div className="d-block d-lg-none mr-2">
                <Link
                  to="#"
                  onClick={(e) => closeUserChat(e)}
                  className="user-chat-remove text-muted font-size-16 p-2"
                >
                  <i className="ri-arrow-left-s-line"></i>
                </Link>
              </div>
              {activeChat.profilePicture !== "Null" ? (
                <div className="mr-3">
                  <img
                    src={activeChat.profilePicture}
                    className="rounded-circle avatar-xs"
                    alt="chatvia"
                  />
                </div>
              ) : (
                <div className="chat-user-img align-self-center mr-3">
                  <div className="avatar-xs">
                    <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                      {activeChat.name.trim().charAt(0)}
                    </span>
                  </div>
                </div>
              )}

              <Media body className="overflow-hidden">
                <h5 className="font-size-16 mb-0 text-truncate">
                  <Link
                    to="#"
                    onClick={() => ChatProfileSidebar()}
                    className="text-reset user-profile-show"
                  >
                    {activeChat.name}
                  </Link>
                  {(() => {
                    switch (activeChat.status) {
                      case "online":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-success d-inline-block ml-1"></i>
                          </>
                        );

                      case "away":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-warning d-inline-block ml-1"></i>
                          </>
                        );

                      case "offline":
                        return (
                          <>
                            <i className="ri-record-circle-fill font-size-10 text-secondary d-inline-block ml-1"></i>
                          </>
                        );

                      default:
                        return;
                    }
                  })()}
                </h5>
              </Media>
            </Media>
          </Col>
          <Col sm={8} xs={4}>
            <ul className="list-inline user-chat-nav text-right mb-0">
              <li className="list-inline-item">
                {/*
                    // SEARCH
                    <Dropdown isOpen={dropdownOpen} toggle={toggle}>
                    <DropdownToggle
                        color="none"
                        className="btn nav-btn "
                        type="button"
                    >
                        <i className="ri-search-line"></i>
                    </DropdownToggle>
                    
                    <DropdownMenu right className="p-0  dropdown-menu-md">
                        <div className="search-box p-2">
                        <Input
                            type="text"
                            className="form-control bg-light border-0"
                            placeholder="Search.."
                        />
                        </div>
                    </DropdownMenu>
                    </Dropdown>
                */}
              </li>

              <li className="list-inline-item d-none d-lg-inline-block">
                <Button
                  type="button"
                  color="none"
                  onClick={() => ChatProfileSidebar()}
                  className="nav-btn user-profile-show"
                >
                  <i className="ri-user-2-line"></i>
                </Button>
              </li>

              <li className="list-inline-item">
                <Dropdown isOpen={dropdownOpen} toggle={toggle}>
                  <DropdownToggle
                    className="btn nav-btn "
                    color="none"
                    type="button"
                  >
                    <i className="ri-more-fill"></i>
                  </DropdownToggle>
                  <DropdownMenu right>
                    <DropdownItem
                      className="d-block d-lg-none user-profile-show"
                      onClick={() => ChatProfileSidebar()}
                    >
                      View profile{" "}
                      <i className="ri-user-2-line float-right text-muted"></i>
                    </DropdownItem>
                    <DropdownItem>
                      Archive{" "}
                      <i className="ri-archive-line float-right text-muted"></i>
                    </DropdownItem>
                    <DropdownItem>
                      Muted{" "}
                      <i className="ri-volume-mute-line float-right text-muted"></i>
                    </DropdownItem>
                    <DropdownItem onClick={(e) => deleteMessage(e)}>
                      Delete{" "}
                      <i className="ri-delete-bin-line float-right text-muted"></i>
                    </DropdownItem>
                  </DropdownMenu>
                </Dropdown>
              </li>
            </ul>
          </Col>
        </Row>
      </div>
    </React.Fragment>
  );
};

export default ChatRightHead;
