import React, { CSSProperties, useState } from "react";
import {
  Input,
  Modal,
  ModalBody,
  Row,
  Col,
  Container,
  Button,
} from "reactstrap";
import { useCall } from "../contexts/CallContext";
const buttonStyles: CSSProperties = {
  width: "100%",
  marginBottom: "12px",
};
interface IProps {
  show: boolean;
  toggle(): void;
}
const DialPad: React.FC<IProps> = ({ show, toggle }) => {
  const [dialNumber, setDialNumber] = useState<string>("");

  const { setCallNumber } = useCall();

  const onChangeDialNumber = (number: string, dtmf = true) => {
    setDialNumber(number);
    // if (dtmf && this.props.sendDtmf)
    //   this.props.sendDtmf(this.props.incomingCall, number.charAt(number.length - 1))
  };
  const onPaste = (event: React.ClipboardEvent<HTMLInputElement>) => {
    event.preventDefault();
    const clipboardData = event.clipboardData || (window as any).clipboardData;
    const pastedData = clipboardData.getData("Text");
    setDialNumber(pastedData.replace(/[^0-9]/g, ""));
  };

  return (
    <Modal style={{ width: "350px" }} isOpen={show} centered toggle={toggle}>
      <ModalBody>
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Input
            style={{ width: "80%", border: "none" }}
            autoFocus={true}
            value={dialNumber}
            onPaste={onPaste}
            onChange={(event) => onChangeDialNumber(event.target.value)}
          />
        </div>

        <Container>
          <Row>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "1");
                }}
              >
                <span>1</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "2");
                }}
              >
                <span>2</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "3");
                }}
              >
                <span>3</span>
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "4");
                }}
              >
                <span>4</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "5");
                }}
              >
                <span>5</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "6");
                }}
              >
                <span>6</span>
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "7");
                }}
              >
                <span>7</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "8");
                }}
              >
                <span>8</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "9");
                }}
              >
                <span>9</span>
              </Button>
            </Col>
          </Row>
          <Row>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "*");
                }}
              >
                <span>*</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "0");
                }}
              >
                <span>0</span>
              </Button>
            </Col>
            <Col>
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                style={buttonStyles}
                onClick={() => {
                  onChangeDialNumber(dialNumber + "#");
                }}
              >
                <span>#</span>
              </Button>
            </Col>
          </Row>
          <Row
            style={{
              height: 82,
              justifyContent: "space-around",
              display: "flex",
            }}
          >
            <Col
              style={{
                justifyContent: "center",
                display: "flex",
              }}
            >
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                onClick={() => {
                  setDialNumber(dialNumber.substring(0, dialNumber.length - 1));
                }}
              >
                <img
                  alt="dial-pad"
                  src={require("../assets/images/icon-backspace.svg")}
                />
              </Button>
            </Col>
            <Col
              style={{
                justifyContent: "center",
                display: "flex",
              }}
            >
              <Button
                color="link"
                className="text-muted pr-1 text-decoration-none"
                onClick={() => {
                  setCallNumber({
                    number: dialNumber,
                    useAudio: true,
                    useVideo: false,
                  });
                  toggle();
                }}
              >
                <img
                  alt="dial-pad"
                  src={require("../assets/images/icon-call-dial.svg")}
                />
              </Button>
            </Col>
          </Row>
        </Container>
      </ModalBody>
    </Modal>
  );
};

export default DialPad;
