import IChat from "../alias/IChat";
import IContact from "../alias/IContact";
import IGroup from "../alias/IGroup";
interface setChatsType {
  (val: IChat[]): void;
}
interface setGroupsType {
  (val: IGroup[]): void;
}
interface setContactsType {
  (val: IContact[]): void;
}
export const onImgError = (
  chats: IChat[],
  groups: IGroup[],
  contacts: IContact[],
  setChats: setChatsType,
  setGroups: setGroupsType,
  setContacts: setContactsType,
  errorContact: IChat | IGroup
) => {
  if (errorContact) {
    setChats(
      chats.map((cht) => {
        if (cht.jid === errorContact.jid) {
          cht.profilePicture = undefined;
        }
        return cht;
      })
    );
    setGroups(
      groups.map((grp) => {
        if (grp.jid === errorContact.jid) {
          grp.profilePicture = undefined;
        }
        return grp;
      })
    );
    setContacts(
      contacts.map((ctc) => {
        if (ctc.jid === errorContact.jid) {
          ctc.profilePicture = undefined;
        }
        return ctc;
      })
    );
  }
};
