import React from "react";
import { Input, Label } from "reactstrap";
import IGroup from "../alias/IGroup";
import { useGroup } from "../contexts/GroupContext";
import sortGroup from "../helpers/SortGroup";

interface IProps {
  handleCheckGroup(selected: boolean, contact: IGroup): void;
}

const SelectGroup: React.FC<IProps> = ({ handleCheckGroup }) => {
  const { groups } = useGroup();

  return (
    <React.Fragment>
      {sortGroup(groups).map((contact, key) => (
        <div key={key}>
          <div className="p-3 font-weight-bold text-primary">
            {contact.group}
          </div>

          <ul className="list-unstyled contact-list">
            {contact.children.map((child, keyChild) => (
              <li key={keyChild}>
                <div className="custom-control custom-checkbox">
                  <Input
                    type="checkbox"
                    className="custom-control-input"
                    onChange={(e) => handleCheckGroup(e.target.checked, child)}
                    id={"memberCheck" + child.jid}
                    value={child.name}
                  />
                  <Label
                    className="custom-control-label"
                    htmlFor={"memberCheck" + child.jid}
                  >
                    {child.name}
                  </Label>
                </div>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </React.Fragment>
  );
};

export default SelectGroup;
