import React, { CSSProperties, useState } from "react";
import { useCall } from "../contexts/CallContext";
import { tagsRange } from "../services/voice/voicecommunicator";
import { motion } from "framer-motion";
import { height, width } from "../helpers/windowSize";
import {
  Button,
  DropdownItem,
  DropdownMenu,
  DropdownToggle /* Tooltip */,
  UncontrolledDropdown,
} from "reactstrap";
import Swal from "sweetalert2";
import { useTranslation } from "react-i18next";
import ICall from "../alias/ICall";
import { useConference } from "../contexts/ConferenceContext";
import CopyModeratorLinkButtonInActiveCall from "./CopyModeratorLinkButtonInActiveCall";
import CopyParticipantLinkButtonInActiveCall from "./CopyParticipantLinkButtonInActiveCall";
import { IConference } from "../alias/IConference";
import IGroup from "../alias/IGroup";
import IContact from "../alias/IContact";
import { activeChat } from "../data/chat";
import { useAuth } from "../contexts/AuthContext";

interface DocumentPictureInPicture extends Document {
  pictureInPictureEnabled: boolean;
  pictureInPictureElement: HTMLVideoElementPictureInPicture;
  exitPictureInPicture(): Promise<any>;
}

interface HTMLVideoElementPictureInPicture extends HTMLVideoElement {
  requestPictureInPicture(): Promise<any>;
  disablePictureInPicture: any;
}

const VideoBox: React.FC = () => {
  const { t } = useTranslation();
  const { activeConference } = useConference();
  const toggleOpacity = () => {
    if (flagToTimeOut === true) {
      setFlagToOpacity(true);
      setFlagToTimeOut(false);
      setTimeout(() => {
        setFlagToOpacity(false);
        setFlagToTimeOut(true);
      }, 6000);
    }
  };
  const [flagToOpacity, setFlagToOpacity] = useState<boolean>(false);
  const [flagToTimeOut, setFlagToTimeOut] = useState<boolean>(true);
  const [widthBox, setWidthBox] = useState(400);
  const [
    pictureInPictureEnabled,
    setPictureInPictureEnabled,
  ] = useState<boolean>(false);

  const [pictureInPictureTag, setPictureInPictureTag] = useState<
    number | undefined
  >(undefined);

  const { currentCalls, setConferenceCommand } = useCall();
  const { user } = useAuth();

  const togglePictureInPicture = (tagId: number) => {
    const doc = document as DocumentPictureInPicture;
    if (!doc.pictureInPictureElement && doc.pictureInPictureEnabled) {
      const video = document.querySelector(
        `#remote-stream-${tagId}`
      ) as HTMLVideoElementPictureInPicture;
      video?.requestPictureInPicture().catch((error) => {});
    } else {
      if (doc.pictureInPictureEnabled) {
        (document as DocumentPictureInPicture)
          .exitPictureInPicture()
          .catch((error) => {});
      }
    }
  };

  const listenPictureInPictureChanges = () => {
    tagsRange.forEach((tagId) => {
      const videoEl = document.querySelector(`#remote-stream-${tagId}`);
      if (
        videoEl &&
        !(videoEl as HTMLVideoElementPictureInPicture).disablePictureInPicture
      ) {
        videoEl.addEventListener("enterpictureinpicture", () => {
          // Video entered Picture-in-Picture mode.
          setPictureInPictureEnabled(true);
          setPictureInPictureTag(tagId);
        });
        videoEl.addEventListener("leavepictureinpicture", () => {
          // Video left Picture-in-Picture mode.
          setPictureInPictureEnabled(false);
          setPictureInPictureTag(undefined);
        });
      }
    });
  };

  const removeListeners = () => {
    tagsRange.forEach((tagId) => {
      const videoEl = document.querySelector(`#remote-stream-${tagId}`);
      if (videoEl) {
        videoEl.removeEventListener("enterpictureinpicture", () => {});
        videoEl.removeEventListener("leavepictureinpicture", () => {});
      }
    });
  };

  const isVisible = (tagId: number) => {
    const callTag = getCallByTag(tagId);
    return (
      callTag &&
      pictureInPictureTag !== tagId &&
      (callTag.sendingVideo || callTag?.receivingVideo)
    );
  };

  const getCallByTag = (tagId: number) => {
    return Array.from(currentCalls.values()).find(
      (call) => call.tagId === `remote-stream-${tagId}`
    );
  };
  const getCallerObjectId = (callerObject: IConference | IGroup | IContact) => {
    return (
      (callerObject as IConference).id ||
      (callerObject as IGroup).groupId ||
      (callerObject as IContact).id
    );
  };
  const isConference = (call: ICall | undefined) => {
    if (call)
      return !!(
        (call.callerObject as IConference).participantPin ||
        (call.callerObject as IGroup).groupId
      );
    return false;
  };
  const sendConfCommand = (
    call: ICall | undefined,
    command: "tvmute" | "tmute"
  ) => {
    if (call) {
      if (command === "tmute") {
        call.sendDTMF("0");
      } else if (command === "tvmute") {
        call.sendDTMF("*");
      }
    }
  };
  removeListeners();
  listenPictureInPictureChanges();

  /*   const [tooltipOpen1, setTooltipOpen1] = useState(false);
  const [tooltipOpen2, setTooltipOpen2] = useState(false);
  const [tooltipOpen3, setTooltipOpen3] = useState(false);
  const [tooltipOpen4, setTooltipOpen4] = useState(false);
  const [tooltipOpen5, setTooltipOpen5] = useState(false);
  const [tooltipOpen6, setTooltipOpen6] = useState(false);
  const [tooltipOpen7, setTooltipOpen7] = useState(false);
  const [tooltipOpen8, setTooltipOpen8] = useState(false);
  const [tooltipOpen9, setTooltipOpen9] = useState(false);
  const [tooltipOpen10, setTooltipOpen10] = useState(false);
  const [tooltipOpen11, setTooltipOpen11] = useState(false); */
  /* 
  const toggleTooltip1 = () => setTooltipOpen1(!tooltipOpen1);
  const toggleTooltip2 = () => setTooltipOpen2(!tooltipOpen2);
  const toggleTooltip3 = () => setTooltipOpen3(!tooltipOpen3);
  const toggleTooltip4 = () => setTooltipOpen4(!tooltipOpen4);
  const toggleTooltip5 = () => setTooltipOpen5(!tooltipOpen5);
  const toggleTooltip7 = () => setTooltipOpen7(!tooltipOpen7);
  const toggleTooltip8 = () => setTooltipOpen8(!tooltipOpen8);
  const toggleTooltip9 = () => setTooltipOpen9(!tooltipOpen9);
  const toggleTooltip10 = () => setTooltipOpen10(!tooltipOpen10);
  const toggleTooltip11 = () => setTooltipOpen11(!tooltipOpen11);
  const toggleTooltip6 = () => setTooltipOpen6(!tooltipOpen6); */
  const layouts = (call: ICall | undefined) => {
    if (call?.callerObject && isConference(call)) {
      const conferenceId = getCallerObjectId(call.callerObject);
      const layoutsName: any = {
        "group:grid": "Distribute in degree",
        "group:grid-zoom": "Highlight users",
        "1up_top_left+9": "Highlight who talks to others around",
        overlaps: "Highlight who talks to others below",
        "presenter-overlap-small-bot-right": "Highlight presentation",
      };
      return Object.keys(layoutsName).map((k) => (
        <DropdownItem
          onClick={() => {
            setConferenceCommand({
              command: "setLayout",
              conferenceId,
              extraParam: k,
            });
          }}
        >
          <span className="text-muted">{t(layoutsName[k])}</span>
        </DropdownItem>
      ));
    }
  };
  const boxVideo: CSSProperties = {
    position: "fixed",
    right: "50px",
    bottom: "50px",
    zIndex: 100,
    maxWidth: `${widthBox}px`,
    borderRadius: "2px",
    backgroundColor: "#000000",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  };

  const video: CSSProperties = {
    position: "relative",
    width: `${widthBox}px`,
  };

  const optionBox: CSSProperties = {
    position: "absolute",
    zIndex: 10,
    top: "2%",
    right: "2%",
    color: "#ffffff",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };

  const option: CSSProperties = {
    cursor: "pointer",
    marginLeft: "5px",
    fontSize: "18px",
    padding: "5px",
  };

  const buttonBox: CSSProperties = {
    position: "absolute",
    zIndex: 10,
    width: "100%",
    bottom: 0,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "row",
  };

  const plus: CSSProperties = {
    position: "absolute",
    bottom: "10px",
    right: "10px",
    zIndex: 13,
    color: "black",
  };

  const minus: CSSProperties = {
    position: "absolute",
    bottom: "10px",
    right: "30px",
    zIndex: 13,
    color: "black",
  };

  const amIaModerator =
    activeConference.conferenceRoomParticipants?.find((member) => member.userUuid === user.id)?.userModerator ===
  true;

  console.log('AMIAMODERATOR', amIaModerator);

  return (
    <div>
      {tagsRange.map((tagId) => (
        <motion.div
          drag
          dragConstraints={{
            top: (height() - 400) * -1,
            bottom: height() - 1100,
            left: (width() - 500) * -1,
            right: 0,
          }}
          style={{
            ...boxVideo,
            display: isVisible(tagId) ? boxVideo.display : "none",
          }}
          key={`remote-stream-${tagId}`}
        >
          <div style={optionBox}>
            {flagToOpacity &&
            (document as DocumentPictureInPicture).pictureInPictureEnabled ? (
              <>
                {!pictureInPictureEnabled && (
                  <div
                    style={option}
                    onClick={() => togglePictureInPicture(tagId)}
                    id="TooltipExample1"
                  >
                    <i
                      style={{
                        backgroundColor: "white",
                        borderRadius: 4,
                        color: "black",
                      }}
                      className="ri-picture-in-picture-2-line"
                    />
                  </div>
                )}
                {pictureInPictureTag === tagId && (
                  <div
                    style={option}
                    onClick={() => togglePictureInPicture(tagId)}
                  >
                    <i
                      style={{
                        backgroundColor: "white",
                        borderRadius: 4,
                        color: "black",
                      }}
                      className="ri-picture-in-picture-exit-line"
                    ></i>
                  </div>
                )}
                <div
                  style={option}
                  onClick={() => {
                    if (widthBox === width() - 300) {
                      setWidthBox(400);
                    } else {
                      setWidthBox(width() - 300);
                    }
                  }}
                >
                  <i
                    style={{
                      backgroundColor: "white",
                      borderRadius: 4,
                      color: "black",
                    }}
                    className="ri-window-2-fill"
                    id="TooltipExample2"
                  ></i>
                </div>
              </>
            ) : null}

            {flagToOpacity && (
              <div
                style={option}
                onClick={() => {
                  const call = getCallByTag(tagId);
                  call?.endCall && call.endCall();
                }}
              >
                <i
                  style={{
                    backgroundColor: "white",
                    borderRadius: 4,
                    color: "black",
                  }}
                  className="ri-close-line"
                  id="TooltipExample3"
                ></i>
              </div>
            )}
            {flagToOpacity && activeConference && amIaModerator && (
              <div /* id="TooltipExample4" */>
                <CopyModeratorLinkButtonInActiveCall
                  moderatorLink={
                    activeConference?.anonymousModeratorUrl as string
                  }
                  buttonIcon={"ri-link-unlink"}
                />
              </div>
            )}
            {flagToOpacity && activeConference && amIaModerator && (
              <div /* id="TooltipExample5" */>
                <CopyParticipantLinkButtonInActiveCall
                  participantLink={
                    activeConference?.anonymousParticipantUrl as string
                  }
                  buttonIcon={"ri-links-line"}
                />
              </div>
            )}
          </div>

          {flagToOpacity && (
            <>
              <div style={buttonBox}>
                <Button
                  type="button"
                  color="none"
                  onClick={() => {
                    const call = getCallByTag(tagId);
                    call?.endCall && call.endCall();
                  }}
                  className="nav-btn user-profile-show btn-head-conference red"
                  /* id="TooltipExample6" */
                >
                  <i className="ri-phone-line"></i>
                </Button>

                <Button
                  type="button"
                  color="none"
                  /* id="TooltipExample7" */
                  onClick={() => {
                    if (getCallByTag(tagId)?.micMuted) {
                      getCallByTag(tagId)?.unMuteMic();
                    } else {
                      getCallByTag(tagId)?.muteMic();
                    }
                    sendConfCommand(getCallByTag(tagId), "tmute");
                  }}
                  className={`nav-btn user-profile-show btn-head-conference ${
                    getCallByTag(tagId)?.micMuted ? "red" : "white"
                  }`}
                >
                  {!getCallByTag(tagId)?.micMuted ? (
                    <i className="ri-mic-line"></i>
                  ) : (
                    <i className="ri-mic-off-line"></i>
                  )}
                </Button>

                <Button
                  type="button"
                  color="none"
                  /* id="TooltipExample8" */
                  onClick={() => {
                    if (getCallByTag(tagId)?.hold)
                      getCallByTag(tagId)?.unHoldCall();
                    else getCallByTag(tagId)?.holdCall();
                  }}
                  className={`nav-btn user-profile-show btn-head-conference ${
                    getCallByTag(tagId)?.hold ? "red" : "white"
                  }`}
                >
                  {getCallByTag(tagId)?.hold ? (
                    <i className="ri-play-line"></i>
                  ) : (
                    <i className="ri-pause-line"></i>
                  )}
                </Button>

                <Button
                  type="button"
                  color="none"
                  /* id="TooltipExample9" */
                  onClick={() => {
                    if (getCallByTag(tagId)?.sendingVideo) {
                      if (getCallByTag(tagId)?.videoMuted) {
                        getCallByTag(tagId)?.enableCam();
                      } else {
                        getCallByTag(tagId)?.disableCam();
                      }
                      sendConfCommand(getCallByTag(tagId), "tvmute");
                    } else {
                      alert(
                        "Mudança de tipo de chamada ainda não está disponivel :("
                      );
                    }
                  }}
                  className={`nav-btn user-profile-show btn-head-conference white ${
                    getCallByTag(tagId)?.videoMuted ? "red" : "white"
                  }`}
                >
                  {getCallByTag(tagId)?.videoMuted ? (
                    <i className="ri-camera-off-line"></i>
                  ) : (
                    <i className="ri-camera-line"></i>
                  )}
                </Button>
                {isConference(getCallByTag(tagId)) && (
                  <UncontrolledDropdown className="align-self-start box-top-right">
                    <DropdownToggle tag="a">
                      <Button
                        type="button"
                        color="none"
                        /* id="TooltipExample9" */
                        onClick={() => {}}
                        className={`nav-btn user-profile-show btn-head-conference white`}
                      >
                        <i className="ri-layout-4-fill" />
                      </Button>
                    </DropdownToggle>
                    <DropdownMenu>{layouts(getCallByTag(tagId))}</DropdownMenu>
                  </UncontrolledDropdown>
                )}
                {isConference(getCallByTag(tagId)) && (
                  <Button
                    type="button"
                    color="none"
                    /* id="TooltipExample9" */
                    onClick={() => {
                      const call = getCallByTag(tagId);
                      if (call?.shareScreen) {
                        call.shareScreen();
                      }
                    }}
                    className={`nav-btn user-profile-show btn-head-conference white`}
                  >
                    <img
                      alt="Screen Share"
                      src={require("../assets/images/ic-screen-share.svg")}
                    />
                  </Button>
                )}
              </div>

              <div style={plus} /* id="TooltipExample10" */>
                <span
                  style={{ ...option, fontSize: "14px" }}
                  onClick={() => {
                    setWidthBox(widthBox + 100);
                  }}
                >
                  <i
                    style={{ backgroundColor: "white", borderRadius: 4 }}
                    className="ri-add-line"
                  ></i>
                </span>
              </div>

              <div style={minus} /* id="TooltipExample11" */>
                <span
                  style={{ ...option, fontSize: "14px" }}
                  onClick={() => {
                    if (widthBox > 400) {
                      setWidthBox(widthBox - 100);
                    } else {
                      Swal.fire({
                        position: "top-end",
                        icon: "error",
                        title: t("Size can not be smaller"),
                        showConfirmButton: false,
                        timer: 3000,
                        customClass: {
                          popup: "swal-mini-popup",
                        },
                      });
                    }
                  }}
                >
                  <i
                    style={{ backgroundColor: "white", borderRadius: 4 }}
                    className="ri-subtract-line"
                  ></i>
                </span>
              </div>
            </>
          )}
          <video
            style={video}
            onMouseOver={toggleOpacity}
            id={`remote-stream-${tagId}`}
            hidden={!isVisible(tagId)}
          />
          <ul
            style={{
              position: "absolute",
              background: "white",
              border: "1px solid #000",
              width: "100%",
              minHeight: "100px",
              left: 0,
              bottom: "-116px",
            }}
          >
            {getCallByTag(tagId)?.conferenceParticipants?.map((participant) => {
              return (
                <li key={participant.memberId}>
                  {participant.callerName} -
                  <span
                    onClick={(e) => {
                      e.preventDefault();
                      setConferenceCommand({
                        command: "tmute",
                        conferenceId: participant.conferenceName,
                        extraParam: `${participant.memberId}`,
                      });
                    }}
                  >
                    {!participant.mute ? (
                      <i className="ri-mic-off-line" />
                    ) : (
                      <i className="ri-mic-line" />
                    )}{" "}
                    |
                  </span>
                  <span
                    onClick={(e) => {
                      e.preventDefault();
                      setConferenceCommand({
                        command: "tvmute",
                        conferenceId: participant.conferenceName,
                        extraParam: `${participant.memberId}`,
                      });
                    }}
                  >
                    {" "}
                    {!participant.video ? (
                      <i className="ri-camera-off-line" />
                    ) : (
                      <i className="ri-camera-line" />
                    )}{" "}
                  </span>
                  {participant.talking ? (
                    <i className="ri-volume-up-line" />
                  ) : (
                    <i className="ri-volume-down-line" />
                  )}
                </li>
              );
            })}
          </ul>
          {/*           <Tooltip
            placement="right"
            isOpen={tooltipOpen1}
            target="TooltipExample1"
            toggle={toggleTooltip1}
          >
            {t(
              "Picture-in-picture"
            )}
          </Tooltip>

          <Tooltip
            placement="right"
            isOpen={tooltipOpen2}
            target="TooltipExample2"
            toggle={toggleTooltip2}
          >
            {t(
              "Maximize/minimize"
            )}
          </Tooltip>

          <Tooltip
            placement="right"
            isOpen={tooltipOpen3}
            target="TooltipExample3"
            toggle={toggleTooltip3}
          >
            {t(
              "End call"
            )}
          </Tooltip>

          <Tooltip
            placement="right"
            isOpen={tooltipOpen4}
            target="TooltipExample4"
            toggle={toggleTooltip4}
          >
            {t("Copy moderator invitation link")}
          </Tooltip> */}

          {/* <Tooltip
            placement="right"
            isOpen={tooltipOpen5}
            target="TooltipExample5"
            toggle={toggleTooltip5}
          >
            {t("Copy participant invitation link")}
          </Tooltip>

          <Tooltip
            placement="right"
            isOpen={tooltipOpen6}
            target="TooltipExample6"
            toggle={toggleTooltip6}
          >
            {t("End call")}
          </Tooltip>

          <Tooltip
            placement="right"
            isOpen={tooltipOpen7}
            target="TooltipExample7"
            toggle={toggleTooltip7}
          >
            {t("Mute/unmute")}
          </Tooltip>

          <Tooltip
            placement="right"
            isOpen={tooltipOpen8}
            target="TooltipExample8"
            toggle={toggleTooltip8}
          >
            {t("Play/pause")}
          </Tooltip>

          <Tooltip
            placement="right"
            isOpen={tooltipOpen9}
            target="TooltipExample9"
            toggle={toggleTooltip9}
          >
            {t("Enable/disable cam")}
          </Tooltip>
          <Tooltip
            placement="right"
            isOpen={tooltipOpen10}
            target="TooltipExample10"
            toggle={toggleTooltip10}
          >
            {t("Zoom in")}
          </Tooltip>
          <Tooltip
            placement="right"
            isOpen={tooltipOpen11}
            target="TooltipExample11"
            toggle={toggleTooltip11}
          >
            {t("Zoom out")}
          </Tooltip> */}
        </motion.div>
      ))}
    </div>
  );
};

export default VideoBox;
