import React, { useEffect, useState, useRef } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  CardBody,
  Button,
  ModalFooter,
} from "reactstrap";

import { useModal } from "../contexts/ModalContext";
import { useContact } from "./../contexts/ContactContext";
import { useChat } from "../contexts/ChatContext";
import { useGroup } from "../contexts/GroupContext";
import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";
import SimpleBar from "simplebar-react";
import SelectMember from "./SelectMember";
import Swal from "sweetalert2";

const ModalAddContacts: React.FC = () => {
  const [search, setSearch] = useState("");
  const { activeChat } = useChat();
  const { groups } = useGroup();

  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);

  const { t } = useTranslation();
  const {
    setToggleAddingMembers,
    setCheckedMembers,
    setGroupToInsertMember,
  } = useContact();

  const { showModalAddMember, setShowModalAddMember } = useModal();

  const toggle = () => {
    if (showModalAddMember) {
      setShowModalAddMember(!showModalAddMember);
      setSelectedContact([]);
      setSearch("");
    }
  };

  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      setSelectedContact([...selectedContact, contact])
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.id === contact.id) {
            return false;
          }
          return true;
        })
      );
    }
  };

  const inputSearch = useRef<HTMLInputElement>(null);

  useEffect(() => {
    setTimeout(() => inputSearch.current?.focus(), 500);
  }, [showModalAddMember]);

  return (
    <Modal
      backdrop="static"
      isOpen={!!showModalAddMember}
      centered
      toggle={toggle}
    >
      <ModalHeader  toggle={toggle}>{t("Add Member")}</ModalHeader>
      <ModalBody>
        <CardBody className="p-2 border">
          <div className="pt-2 pb-3 border-bottom">
            <input
              ref={inputSearch}
              type="text"
              className="form-control mb-3"
              id="addgroupname-input"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              placeholder={t("Search users..")}
            />
            <hr />
            <SimpleBar style={{ height: "500px" }}>
              <SelectMember
                handleCheckContact={handleCheckContact}
                search={search}
                checkedContacts={selectedContact}
              />
            </SimpleBar>
          </div>
          <ModalFooter className="border-0">
            <Button
              color="default"
              onClick={() => {
                setSelectedContact([]);
                toggle();
              }}
            >
              {t("Close")}
            </Button>
            <Button
              color="primary"
              onClick={() => {
                Swal.fire({
                  position: 'top-end',
                  icon: 'success',
                  title: 'Added!',
                  showConfirmButton: false,
                  timer: 1500,
                  customClass: {
                    popup: "swal-mini-popup"
                  }
                })
                setToggleAddingMembers(true);
                toggle();
                groups.forEach((grp) => {
                  if (grp.groupId === activeChat.groupId) {
                    setGroupToInsertMember(grp);
                    setCheckedMembers(selectedContact);
                  }
                });
              }}
            >
              {t("Add Contacts")}
            </Button>
          </ModalFooter>
        </CardBody>
      </ModalBody>
    </Modal>
  );
};

export default ModalAddContacts;
