import React, { useEffect, useState } from "react";
import {
  Button,
  Row,
  Col,
  UncontrolledTooltip,
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  Label,
} from "reactstrap";
import { Picker, Emoji } from "emoji-mart";
import "emoji-mart/css/emoji-mart.css";
import { ReactMic, ReactMicStopEvent } from "react-mic";
import { useChat } from "../contexts/ChatContext";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import { useAuth } from "../contexts/AuthContext";
import { blobToFile } from "../helpers/AudioUtils";
import { useTranslation } from "react-i18next";
import ChatInputAttachedFileButton from "./ChatInputAttachedFileButton";
import ReactTextareaAutocomplete from "@webscopeio/react-textarea-autocomplete";
import IMember from "../alias/IMember";
//import { HashLink as Link } from "react-router-hash-link";

const ChatInput: React.FC = () => {
  const { activeChat, setActiveChat } = useChat();
  const { user } = useAuth();

  const [textMessage, setTextMessage] = useState("");
  const [isOpen, setisOpen] = useState(false);
  const [record, setRecord] = useState(false);
  const [deleteAudio, setDeleteAudio] = useState(false);
  const [audioFile, setAudioFile] = useState<File | undefined>(undefined);
  const toggle = () => setisOpen(!isOpen);
  const { t } = useTranslation();
  const members = activeChat.members;

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setTextMessage(e.target.value);
  };

  const addEmoji = (e: any) => {
    let emoji = e.native;
    setTextMessage(textMessage + emoji);
  };

  const onData = (recordedBlob: any) => {
    console.log("chunk of real-time data is: ", recordedBlob);
  };

  const onStop = (recordedBlob: ReactMicStopEvent) => {
    setAudioFile(blobToFile(recordedBlob.blob, `${recordedBlob.blob}.webm`));
  };

  const onKeyPress = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      onAddMessage();
      e.preventDefault();
    }
  };

  const handlePaste = (e: React.ClipboardEvent<HTMLTextAreaElement>) => {
    e.persist();
    console.log(e);
    let files = e.clipboardData.files;
    console.log(e.clipboardData.getData("Text"));

    if (files.length === 0) return;
    let file = files[0];
    if (file.size > 5242880) {
      alert(
        "Tamanho de arquivo " +
          file.name +
          " não suportado. Por favor, envie um arquivo menor do que 5mb!"
      );
      return;
    } else {
      onPasteMessage(file);
    }
    console.log("arquivo enviado");
  };

  const onPasteMessage = (file: File) => {
    let chat = { ...activeChat };

    chat.messages.push({
      id: `${activeChat.messages.length + 1}`,
      userName: user.name,
      message: "Carregando...",
      time: "",
      isImageMessage: false,
      isFileMessage: false,
      isVideoMessage: false,
      isAudioMessage: false,
      fileUrl: "",
      sendFile: file,
      toUser: activeChat.id,
      fromUser: user.id,
      deliverStatus: DeliverStatus.SENDING,
      readStatus: ReadStatus.UNREAD,
      deliverTimestamp: "",
      readTimestamp: "",
      content: "metadata",
      contentType: "type",
      isReplyMessage: false,
      isForwardMessage: false,
    });

    setActiveChat(chat);
  };

  const onAddMessage = () => {
    if (textMessage.trim() !== "") {
      let chat = { ...activeChat };

      chat.messages.push({
        id: `${activeChat.messages.length + 1}`,
        userName: user.name,
        message: textMessage.trim(),
        time: "",
        isImageMessage: false,
        isFileMessage: false,
        isVideoMessage: false,
        isAudioMessage: false,
        fileUrl: "",
        sendFile: undefined,
        toUser: activeChat.id,
        fromUser: user.id,
        deliverStatus: DeliverStatus.SENDING,
        readStatus: ReadStatus.UNREAD,
        deliverTimestamp: "",
        readTimestamp: "",
        content: "metadata",
        contentType: "type",
        isReplyMessage: false,
        isForwardMessage: false,
      });
      setActiveChat(chat);
    }

    setTextMessage("");
  };

  const sendAudio = () => {
    if (!deleteAudio) {
      audioFile && onPasteMessage(audioFile);
    } else {
      setDeleteAudio(false);
    }
  };
  const updateFocus = () => {
    console.log("focus");
  };
  useEffect(sendAudio, [audioFile]);
  useEffect(updateFocus, [activeChat.id]);
  return (
    <React.Fragment>
      <div className="p-3 p-lg-4 border-top mb-0">
        <div>
          <Row noGutters>
            {/*
            <Col>
              <Link smooth to="#957f4a5f-221e-4558-921b-06288ec36962">
                teste
              </Link>
            </Col>
            */}
            <Col>
              <div>
                <ReactMic
                  record={record}
                  className={`sound-wave ${record ? "" : "d-none"}`}
                  onStop={onStop}
                  onData={onData}
                  strokeColor="#7a7f9a"
                  backgroundColor="#e6ebf5"
                />
                <ReactTextareaAutocomplete
                  className="form-control form-control-lg bg-light border-light"
                  style={{ height: 43, resize: "none" }}
                  value={textMessage}
                  onChange={handleChange}
                  onKeyPress={onKeyPress}
                  onPaste={handlePaste}
                  placeholder={t("Send message...")}
                  loadingComponent={() => <span>Loading</span>}
                  listClassName="list-group"
                  listStyle={{
                    textAlign: "start",
                    position: "absolute",
                    bottom: 46.3,
                    width: "100%",
                  }}
                  itemClassName="list-group-item"
                  trigger={{
                    "@": {
                      dataProvider: (token) => {
                        if (members) {
                          return members.filter((item) => {
                            return item.name
                              .toUpperCase()
                              .match(token.toUpperCase());
                          });
                        }
                        return [];
                      },
                      component: Item,
                      output: (item, trigger) =>
                        "<squadtag>" +
                        JSON.stringify({
                          userId: item.userId,
                          name: item.name,
                        }) +
                        "<squadtag/>",
                      allowWhitespace: true,
                    },
                  }}
                />
                <div
                  style={{
                    color: "red",
                    fontSize: 12,
                  }}
                >
                  {textMessage.length > 9900
                    ? 10000 - textMessage.length + " caracteres restantes"
                    : null}
                </div>
              </div>
            </Col>
            <Col xs="auto">
              <div className="chat-input-links ml-md-2">
                <ul className="list-inline mb-0">
                  {!record ? (
                    <li className="list-inline-item">
                      <ButtonDropdown
                        className="emoji-dropdown"
                        direction="up"
                        isOpen={isOpen}
                        toggle={toggle}
                      >
                        <DropdownToggle
                          id="emoji"
                          color="link"
                          className="text-decoration-none font-size-16 btn-lg waves-effect"
                        >
                          <i className="ri-emotion-happy-line"></i>
                        </DropdownToggle>
                        <DropdownMenu className="dropdown-menu-lg-right">
                          <Picker set="apple" onSelect={addEmoji} />
                          <Emoji emoji="santa" set="apple" size={16} />
                        </DropdownMenu>
                      </ButtonDropdown>
                      <UncontrolledTooltip target="emoji" placement="top">
                        {t("Emoji")}
                      </UncontrolledTooltip>
                    </li>
                  ) : (
                    <li className="list-inline-item input-file">
                      <Label
                        id="audio"
                        className={`btn btn-link text-decoration-none font-size-16 btn-lg waves-effect`}
                        onClick={() => {
                          setDeleteAudio(true);
                          setRecord(false);
                        }}
                      >
                        <i className="ri-delete-bin-5-line" />
                      </Label>
                      <UncontrolledTooltip target="audio" placement="top">
                        {t("Stop audio")}
                      </UncontrolledTooltip>
                    </li>
                  )}
                  <li className="list-inline-item input-file">
                    <Label
                      id="audio"
                      className={`btn btn-link text-decoration-none font-size-16 btn-lg waves-effect ${
                        record ? "text-record" : ""
                      }`}
                      onClick={() => setRecord(!record)}
                    >
                      {record ? (
                        <i className="ri-stop-circle-line" />
                      ) : (
                        <i className="ri-mic-line" />
                      )}
                    </Label>
                    <UncontrolledTooltip target="audio" placement="top">
                      {t("Audio")}
                    </UncontrolledTooltip>
                  </li>
                  <ChatInputAttachedFileButton />

                  <li className="list-inline-item">
                    <Button
                      type={textMessage.length < 10000 ? "submit" : "button"}
                      color="primary"
                      className="font-size-16 btn-lg chat-send waves-effect waves-light"
                      onClick={() => onAddMessage()}
                    >
                      <i className="ri-send-plane-2-fill"></i>
                    </Button>
                  </li>
                </ul>
              </div>
            </Col>
          </Row>
        </div>
      </div>
    </React.Fragment>
  );
};

export default ChatInput;

interface IItems {
  entity: IMember;
}

const Item: React.FC<IItems> = ({ entity }) => <div>{`${entity.name}`}</div>;
