import React from "react";
import SimpleBar from "simplebar-react";
import {
  Media,
} from "reactstrap";

import goldenstar from "./../assets/images/ic-star-fill.svg"
import star from "./../assets/images/ic-star.svg"
import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";
import sortContactGroup from "../helpers/SortContactGroup";
import { useContact } from "../contexts/ContactContext";

interface IProps {
  recentContactList: IContact[];
}

const ContactList: React.FC<IProps> = ({ recentContactList }) => {
  const { openContact, setNewFavorite } = useContact();

  return (
    <SimpleBar
      style={{ maxHeight: "100%" }}
      id="chat-room"
      className="p-4 chat-message-list chat-group-list"
    >
      {sortContactGroup(recentContactList).map((contactsGroupList, key) => (
        <div key={key} className={key + 1 === 1 ? "" : "mt-3"}>
          <div className="p-3 font-weight-bold text-primary">
            {contactsGroupList.group}
          </div>
          <ul className="list-unstyled contact-list">
            {contactsGroupList.children.map((contact, key) => (
              <li key={key} onClick={() => openContact(contact)}>
                <Media className="align-items-center">
                  <Media body>
                    <h5 className="font-size-14 m-0">{contact.name}</h5>
                    <div style={{display: "flex",
                    justifyContent: "flex-end",
                    alignItems: "flex-end",
                    position: "relative",
                    bottom: 13.5
                    }}>
                    {<img alt="star" onClick=
                        {() =>
                          {
                          setNewFavorite(contact);
                          }
                        }
                        src={contact.favorite ? goldenstar : star} style={{position: "relative", right: 2, top: 2}}
                    />}
                    </div>
                  </Media>{/* 
                  //3 PONTOS AO LADO DA ESTRELINHA DO CONTATO NA LISTA DE CONTATOS PARA OPÇÕES
                  <UncontrolledDropdown>
                    <DropdownToggle tag="a" className="text-muted">
                      <i className="ri-more-2-fill"></i>
                    </DropdownToggle>
                    <DropdownMenu right>
                      <DropdownItem>
                        {t("Share")}{" "}
                        <i className="ri-share-line float-right text-muted"></i>
                      </DropdownItem>
                      <DropdownItem>
                        {t("Block")}{" "}
                        <i className="ri-forbid-line float-right text-muted"></i>
                      </DropdownItem>
                      <DropdownItem onClick={() => setRemovedContact(contact)}>
                        {t("Remove")}{" "}
                        <i className="ri-delete-bin-line float-right text-muted"></i>
                      </DropdownItem>
                    </DropdownMenu>
                  </UncontrolledDropdown> */}
                </Media>
              </li>
            ))}
          </ul>
        </div>
      ))}
    </SimpleBar>
  );
};

export default ContactList;
