import React from 'react';
import { Button } from 'reactstrap';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import useCopy from "@react-hook/copy";
import { useTranslation } from 'react-i18next';
import { toastSuccess } from '../helpers/toastCaller';

export interface ICopyParticipantLinkButton{
    moderatorLink: string,
    buttonIcon: string
}

const CopyParticipantLinkButton : React.FC<ICopyParticipantLinkButton> = ({moderatorLink, buttonIcon}) => {
    const { copy } = useCopy(moderatorLink);
    const { t } = useTranslation();
    return(
        <>
            <ToastContainer/>
            <Button
            type="button"
            color="none"
            onClick={() => {
            copy();
            toastSuccess(t('Copied to clipboard a moderator invite!'));
            }}
            className="nav-btn user-profile-show"
            >
            <i className={buttonIcon}></i>
            </Button>
        </>
    )
}

export default CopyParticipantLinkButton;
