import React, { CSSProperties, useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { toast } from "react-toastify";
import {
  Button,
  DropdownMenu,
  DropdownToggle,
  Tooltip,
  UncontrolledDropdown,
} from "reactstrap";
import ICall from "../alias/ICall";
import { DocumentPictureInPicture } from "../alias/IDocumentPictureInPicture";
import { useAuth } from "../contexts/AuthContext";
import { useConference } from "../contexts/ConferenceContext";
import { useParticipantsDrawer } from "../contexts/ParticipantsDrawerContext";
import CopyModeratorLinkButtonInActiveCall from "./CopyModeratorLinkButtonInActiveCall";
import CopyParticipantLinkButtonInActiveCall from "./CopyParticipantLinkButtonInActiveCall";

interface IProps {
  tagId: number;
  pictureInPictureTag: number | undefined;
  widthBox: number;
  setWidthBox(value: number): void;
  width(): number;
  height(): number;
  flagToOpacity: boolean;
  pictureInPictureEnabled: boolean;
  togglePictureInPicture(value: number): void;
  sendConfCommand(call: ICall | undefined, command: "tvmute" | "tmute"): void;
  layouts(call: ICall | undefined): JSX.Element[] | undefined;
  getCallByTag(tagId: number): ICall | undefined;
  isConference(call: ICall | undefined): boolean;
}

const VideoBoxButton: React.FC<IProps> = ({
  tagId,
  pictureInPictureTag,
  widthBox,
  setWidthBox,
  width,
  height,
  flagToOpacity,
  pictureInPictureEnabled,
  togglePictureInPicture,
  sendConfCommand,
  layouts,
  getCallByTag,
  isConference,
}) => {
  const { t } = useTranslation();
  const { activeConference } = useConference();
  const { show, setShow, setParticipants } = useParticipantsDrawer();
  const { user } = useAuth();

  const participants = getCallByTag(tagId)?.conferenceParticipants;

  console.log(participants);

  const [tooltipOpen100, setTooltipOpen100] = useState(false);
  const [tooltipOpen101, setTooltipOpen101] = useState(false);
  const [tooltipOpen102, setTooltipOpen102] = useState(false);
  const [tooltipOpen103, setTooltipOpen103] = useState(false);
  const [tooltipOpen104, setTooltipOpen104] = useState(false);
  const [tooltipOpen105, setTooltipOpen105] = useState(false);
  const [tooltipOpen106, setTooltipOpen106] = useState(false);
  const [tooltipOpen107, setTooltipOpen107] = useState(false);
  const [tooltipOpen108, setTooltipOpen108] = useState(false);
  /*   const [tooltipOpen109, setTooltipOpen109] = useState(false);
    const [tooltipOpen110, setTooltipOpen110] = useState(false);  */
  const [tooltipOpen111, setTooltipOpen111] = useState(false);
  const [tooltipOpen112, setTooltipOpen112] = useState(false);

  const toggleTooltip100 = () => setTooltipOpen100(!tooltipOpen100);
  const toggleTooltip101 = () => setTooltipOpen101(!tooltipOpen101);
  const toggleTooltip102 = () => setTooltipOpen102(!tooltipOpen102);
  const toggleTooltip103 = () => setTooltipOpen103(!tooltipOpen103);
  const toggleTooltip104 = () => setTooltipOpen104(!tooltipOpen104);
  const toggleTooltip105 = () => setTooltipOpen105(!tooltipOpen105);
  const toggleTooltip106 = () => setTooltipOpen106(!tooltipOpen106);
  const toggleTooltip107 = () => setTooltipOpen107(!tooltipOpen107);
  const toggleTooltip108 = () => setTooltipOpen108(!tooltipOpen108);
  /*   const toggleTooltip109 = () => setTooltipOpen109(!tooltipOpen109);
    const toggleTooltip110 = () => setTooltipOpen110(!tooltipOpen110); */
  const toggleTooltip111 = () => setTooltipOpen111(!tooltipOpen111);
  const toggleTooltip112 = () => setTooltipOpen112(!tooltipOpen112);

  function update() {
    if (participants) {
      setParticipants(participants);
    } else {
      setParticipants([]);
    }
  }

  useEffect(update, [participants]);

  const optionBox: CSSProperties = {
    position: "absolute",
    zIndex: 10,
    top: "2%",
    right: "2%",
    color: "#ffffff",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };

  const option: CSSProperties = {
    cursor: "pointer",
    marginLeft: "5px",
    fontSize: "18px",
    padding: "5px",
  };

  const buttonBox: CSSProperties = {
    position: "absolute",
    zIndex: 10,
    width: "100%",
    bottom: 0,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "row",
  };

  const plus: CSSProperties = {
    position: "absolute",
    bottom: "10px",
    right: "10px",
    zIndex: 13,
    color: "black",
  };

  const minus: CSSProperties = {
    position: "absolute",
    bottom: "10px",
    right: "30px",
    zIndex: 13,
    color: "black",
  };

  const amIaModerator =
    activeConference?.conferenceRoomParticipants?.find(
      (member) => member.userUuid === user.id
    )?.userModerator === true;

  return (
    <>
      <div style={optionBox}>
        <div id="TooltipExample103">
          {flagToOpacity &&
          (document as DocumentPictureInPicture).pictureInPictureEnabled ? (
            <>
              {flagToOpacity && activeConference && amIaModerator && (
                <div>
                  <CopyModeratorLinkButtonInActiveCall
                    moderatorLink={
                      activeConference?.anonymousModeratorUrl as string
                    }
                    buttonIcon={"ri-link-unlink"}
                  />
                </div>
              )}
            </>
          ) : null}
        </div>

        <div id="TooltipExample104">
          {flagToOpacity &&
            activeConference &&
            amIaModerator &&
            !pictureInPictureEnabled && (
              <CopyParticipantLinkButtonInActiveCall
                participantLink={
                  activeConference?.anonymousParticipantUrl as string
                }
                buttonIcon={"ri-links-line"}
              />
            )}
        </div>

        <div id="TooltipExample102">
          {flagToOpacity && !pictureInPictureEnabled && (
            <div style={option} onClick={() => togglePictureInPicture(tagId)}>
              <i
                style={{
                  backgroundColor: "white",
                  borderRadius: 4,
                  color: "black",
                }}
                className="ri-picture-in-picture-2-line"
              />
            </div>
          )}
        </div>

        <div id="TooltipExample102">
          {pictureInPictureTag === tagId && (
            <div style={option} onClick={() => togglePictureInPicture(tagId)}>
              <i
                style={{
                  backgroundColor: "white",
                  borderRadius: 4,
                  color: "black",
                }}
                className="ri-picture-in-picture-exit-line"
              ></i>
            </div>
          )}
        </div>

        <div id="TooltipExample101">
          {flagToOpacity && (
            <div
              style={option}
              onClick={() => {
                if (widthBox === width() - 300) {
                  setWidthBox(400);
                } else {
                  setWidthBox(width() - 300);
                }
              }}
            >
              <i
                style={{
                  backgroundColor: "white",
                  borderRadius: 4,
                  color: "black",
                }}
                className="ri-window-2-fill"
              />
            </div>
          )}
        </div>

        <div id="TooltipExample1000">
          {flagToOpacity && (
            <div
              style={option}
              onClick={() => {
                setShow(!show);
              }}
            >
              <i
                style={{
                  backgroundColor: "white",
                  borderRadius: 4,
                  color: "black",
                }}
                className="ri-layout-right-line"
              ></i>
            </div>
          )}
        </div>

        <div id="TooltipExample100">
          {flagToOpacity && (
            <div
              style={option}
              onClick={() => {
                const call = getCallByTag(tagId);
                call?.endCall && call.endCall();
              }}
            >
              <i
                style={{
                  backgroundColor: "white",
                  borderRadius: 4,
                  color: "black",
                }}
                className="ri-close-line"
              ></i>
            </div>
          )}
        </div>
      </div>
      <>
        <div style={buttonBox}>
          <div id="TooltipExample112">
            {flagToOpacity && (
              <Button
                type="button"
                color="none"
                onClick={() => {
                  const call = getCallByTag(tagId);
                  call?.endCall && call.endCall();
                }}
                className="nav-btn user-profile-show btn-head-conference red"
                /* id="TooltipExample6" */
              >
                <i className="ri-phone-line"></i>
              </Button>
            )}
          </div>

          <div id="TooltipExample106">
            {flagToOpacity && (
              <Button
                type="button"
                color="none"
                /* id="TooltipExample7" */
                onClick={() => {
                  if (getCallByTag(tagId)?.micMuted) {
                    getCallByTag(tagId)?.unMuteMic();
                  } else {
                    getCallByTag(tagId)?.muteMic();
                  }
                  sendConfCommand(getCallByTag(tagId), "tmute");
                }}
                className={`nav-btn user-profile-show btn-head-conference ${
                  getCallByTag(tagId)?.micMuted ? "red" : "white"
                }`}
              >
                {!getCallByTag(tagId)?.micMuted ? (
                  <i className="ri-mic-line"></i>
                ) : (
                  <i className="ri-mic-off-line"></i>
                )}
              </Button>
            )}
          </div>

          <div id="TooltipExample105">
            {flagToOpacity && (
              <Button
                type="button"
                color="none"
                /* id="TooltipExample8" */
                onClick={() => {
                  if (getCallByTag(tagId)?.hold)
                    getCallByTag(tagId)?.unHoldCall();
                  else getCallByTag(tagId)?.holdCall();
                }}
                className={`nav-btn user-profile-show btn-head-conference ${
                  getCallByTag(tagId)?.hold ? "red" : "white"
                }`}
              >
                {getCallByTag(tagId)?.hold ? (
                  <i className="ri-play-line"></i>
                ) : (
                  <i className="ri-pause-line"></i>
                )}
              </Button>
            )}
          </div>

          <div id="TooltipExample108">
            {flagToOpacity && (
              <Button
                type="button"
                color="none"
                /* id="TooltipExample9" */
                onClick={() => {
                  if (getCallByTag(tagId)?.sendingVideo) {
                    if (getCallByTag(tagId)?.videoMuted) {
                      getCallByTag(tagId)?.enableCam();
                    } else {
                      getCallByTag(tagId)?.disableCam();
                    }
                    sendConfCommand(getCallByTag(tagId), "tvmute");
                  } else {
                    alert(
                      "Mudança de tipo de chamada ainda não está disponivel :("
                    );
                  }
                }}
                className={`nav-btn user-profile-show btn-head-conference white ${
                  getCallByTag(tagId)?.videoMuted ? "red" : "white"
                }`}
              >
                {getCallByTag(tagId)?.videoMuted ? (
                  <i className="ri-camera-off-line"></i>
                ) : (
                  <i className="ri-camera-line"></i>
                )}
              </Button>
            )}
          </div>

          <div id="TooltipExample107">
            {flagToOpacity && isConference(getCallByTag(tagId)) && (
              <UncontrolledDropdown className="align-self-start box-top-right">
                <DropdownToggle tag="a">
                  <Button
                    type="button"
                    color="none"
                    /* id="TooltipExample9" */
                    onClick={() => {}}
                    className={`nav-btn user-profile-show btn-head-conference white`}
                  >
                    <i className="ri-layout-4-fill" />
                  </Button>
                </DropdownToggle>
                <DropdownMenu>{layouts(getCallByTag(tagId))}</DropdownMenu>
              </UncontrolledDropdown>
            )}
          </div>

          <div id="TooltipExample111">
            {flagToOpacity && isConference(getCallByTag(tagId)) && (
              <Button
                type="button"
                color="none"
                /* id="TooltipExample9" */
                onClick={() => {
                  const call = getCallByTag(tagId);
                  if (call?.shareScreen) {
                    call.shareScreen();
                  }
                }}
                className={`nav-btn user-profile-show btn-head-conference white`}
              >
                <img
                  alt="Screen Share"
                  src={require("../assets/images/ic-screen-share.svg")}
                />
              </Button>
            )}
          </div>
        </div>

        <div>
          {flagToOpacity && (
            <div style={plus} /* id="TooltipExample10" */>
              <span
                style={{ ...option, fontSize: "14px" }}
                onClick={() => {
                  setWidthBox(widthBox + 100);
                }}
              >
                <i
                  style={{ backgroundColor: "white", borderRadius: 4 }}
                  className="ri-add-line"
                ></i>
              </span>
            </div>
          )}
        </div>

        <div>
          {flagToOpacity && (
            <div style={minus}>
              <span
                style={{ ...option, fontSize: "14px" }}
                onClick={() => {
                  if (widthBox > 400) {
                    setWidthBox(widthBox - 100);
                  } else {
                    toast.error(t("Size can not be smaller"), {
                      position: "top-center",
                      autoClose: 3000,
                      hideProgressBar: true,
                      closeOnClick: true,
                      pauseOnHover: true,
                      draggable: true,
                      progress: undefined,
                    });
                  }
                }}
              >
                <i
                  style={{ backgroundColor: "white", borderRadius: 4 }}
                  className="ri-subtract-line"
                ></i>
              </span>
            </div>
          )}
        </div>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen100}
          target="TooltipExample100"
          toggle={toggleTooltip100}
        >
          {t("End call")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen101}
          target="TooltipExample101"
          toggle={toggleTooltip101}
        >
          {t("Maximize/minimize")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen102}
          target="TooltipExample102"
          toggle={toggleTooltip102}
        >
          {t("Picture-in-picture")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen103}
          target="TooltipExample103"
          toggle={toggleTooltip103}
        >
          {t("Copy moderator invitation link")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen104}
          target="TooltipExample104"
          toggle={toggleTooltip104}
        >
          {t("Copy participant invitation link")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen105}
          target="TooltipExample105"
          toggle={toggleTooltip105}
        >
          {t("Put call on hold")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen106}
          target="TooltipExample106"
          toggle={toggleTooltip106}
        >
          {t("Mute/unmute")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen107}
          target="TooltipExample107"
          toggle={toggleTooltip107}
        >
          {t("Distribute screen")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen108}
          target="TooltipExample108"
          toggle={toggleTooltip108}
        >
          {t("Enable/disable cam")}
        </Tooltip>

        {/* <Tooltip
                placement="right"
                isOpen={tooltipOpen109}
                target="TooltipExample109"
                toggle={toggleTooltip109}
            >
                {t("Zoom in")}
            </Tooltip>

            <Tooltip
                placement="right"
                isOpen={tooltipOpen110}
                target="TooltipExample110"
                toggle={toggleTooltip110}
            >
                {t("Zoom out")}
            </Tooltip> */}

        <Tooltip
          placement="right"
          isOpen={tooltipOpen111}
          target="TooltipExample111"
          toggle={toggleTooltip111}
        >
          {t("Share screen")}
        </Tooltip>

        <Tooltip
          placement="right"
          isOpen={tooltipOpen112}
          target="TooltipExample112"
          toggle={toggleTooltip112}
        >
          {t("End call")}
        </Tooltip>
      </>
      ;
    </>
  );
};

export default VideoBoxButton;
