import React from "react";
import IContact from "../alias/IContact";

export function TextMessageTagParse(
  value: string,
  contacts: IContact[],
  openContact: any
) {
  let newValue = value.split("<squadtag>");
  let newComp: any[] = [];
  newValue.forEach((element) => {
    let msgTest = element.split("<squadtag/>");
    if (msgTest.length > 1) {
      msgTest.forEach((msg, i) => {
        if (i === 0) {
          let obj = JSON.parse(msg);
          newComp.push(
            React.createElement(
              "a",
              {
                href: "#",
                onClick: () =>
                  openContact(
                    contacts.filter((item) => item.id === obj.userId)[0]
                  ),
              },
              `@${obj.name}`
            )
          );
        } else {
          newComp.push(msg);
        }
      });
    } else {
      newComp.push(msgTest);
    }
  });

  return newComp;
}
