import React, { useState, useEffect, useRef } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Label,
  Input,
  Alert,
} from "reactstrap";

//simple bar
import SimpleBar from "simplebar-react";

//components
import SelectContact from "./SelectContact";

import { useTranslation } from "react-i18next";
import IContact from "../alias/IContact";
import { useContact } from "../contexts/ContactContext";
import EmailList from "./EmailList";
import IEmailList from "../alias/IEmailList";
import { zeroLeft } from "./../helpers/zeroLeft";
import Swal from "sweetalert2";
import { useConference } from "../contexts/ConferenceContext";
import { useChat } from "../contexts/ChatContext";
import { IConference } from "../alias/IConference";
import { DateTime } from "luxon";

interface IProps {
  show: boolean;
  toggle(): void;
}

const today = () => {
  var today = new Date();
  var dd = today.getDate();
  var mm = today.getMonth() + 1; //January is 0!
  var yyyy = today.getFullYear();

  var hh = zeroLeft(today.getHours(), 2);
  var min = zeroLeft(today.getMinutes(), 2);

  return {
    date: `${yyyy}-${mm}-${dd}`,
    time: `${hh}:${min}`,
  };
};

export const ModalEditConference: React.FC<IProps> = ({ show, toggle }) => {
  const { t } = useTranslation();
  const { contacts } = useContact();
  const { activeChat } = useChat();
  const { setConferenceToUpdate, conferenceList } = useConference();
  const [conference, setConference] = useState<IConference | undefined>();

  const [isOpenAlert, setIsOpenAlert] = useState(false);
  const [search, setSearch] = useState("");
  const [message, setMessage] = useState("");

  const [name, setName] = useState<string>("");
  const [description, setDescription] = useState<string>("");
  const [startDate, setStartDate] = useState<string>(today().date);
  const [startTime, setStartTime] = useState<string>(today().time);
  const [duration, setDuration] = useState<string>("01:00");
  const [mdCheck, setMdCheck] = useState<boolean>(false);
  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);
  const [emailList, setEmailList] = useState<IEmailList[]>([]);

  const handleEditConference = () => {
    setConferenceToUpdate(conference);
    setMessage("Editando conferÃªncia...");
    setIsOpenAlert(true);
    setTimeout(() => {
      setIsOpenAlert(false);
      setMessage("");
      setSearch("");
      setName("");
      setDescription("");
      setStartDate(today().date);
      setStartTime(today().time);
      setDuration("01:00");
      setMdCheck(false);
      setSelectedContact([]);
      setEmailList([]);
    }, 2000);
  };

  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      setSelectedContact([...selectedContact, contact]);
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.id === contact.id) {
            return false;
          }
          return true;
        })
      );
    }
  };

  const updatedTime = () => {
    setTime(startTime);
  };

  useEffect(updatedTime, [startDate]);

  const setTime = (value: string) => {
    let time = value.split(":");
    let currentTime = today().time.split(":");

    if (startDate === today().date) {
      if (parseInt(time[0]) < parseInt(currentTime[0])) {
        Swal.fire({
          position: "top-end",
          icon: "error",
          title: "The time has to be longer than the current one!",
          showConfirmButton: false,
          timer: 3000,
          customClass: {
            popup: "swal-mini-popup",
          },
        });
        setStartTime(today().time);
        return;
      }

      if (
        parseInt(time[0]) === parseInt(currentTime[0]) &&
        parseInt(time[1]) < parseInt(currentTime[1])
      ) {
        Swal.fire({
          position: "top-end",
          icon: "error",
          title: "The time has to be longer than the current one!",
          showConfirmButton: false,
          timer: 3000,
          customClass: {
            popup: "swal-mini-popup",
          },
        });
        setStartTime(today().time);
        return;
      }
    }

    setStartTime(value);
  };

  const inputName = useRef<HTMLInputElement>(null);

  function init() {
    const conferenceData = conferenceList.find(
      (conference) => conference.jid === activeChat.jid
    );

    if (conferenceData) {
      setConference(conferenceData);
      setName(conferenceData.name);
      setDescription(
        conferenceData.description ? conferenceData.description : ""
      );

      const startDateData = DateTime.fromString(
        conferenceData.startDatetimeStr,
        "yyyy-MM-dd hh:mm:ss"
      );

      const stopDateData = DateTime.fromString(
        conferenceData.stopDatetimeStr,
        "yyyy-MM-dd hh:mm:ss"
      );

      setStartDate(startDateData.toFormat("yyyy-MM-dd"));
      setStartTime(startDateData.toFormat("HH:mm:ss"));

      const ms = stopDateData.toMillis() - startDateData.toMillis();

      const seconds = Math.floor((ms / 1000) % 60);
      const minutes = Math.floor((ms / (1000 * 60)) % 60);
      const hours = Math.floor((ms / (1000 * 60 * 60)) % 24);

      setDuration(
        `${zeroLeft(hours, 2)}:${zeroLeft(minutes, 2)}:${zeroLeft(seconds, 2)}`
      );

      console.log(conferenceData);

      //conferenceData.startDatetime
      //conferenceData.stopDatetime
    }

    setTimeout(() => inputName.current?.focus(), 500);
  }

  useEffect(init, [show]);

  return (
    <Modal isOpen={show} size="lg" centered toggle={toggle}>
      <ModalHeader
        tag="h5"
        className="modal-title font-size-16"
        toggle={toggle}
      >
        {t("Edit conference")}
      </ModalHeader>
      <ModalBody className="p-4">
        <Form>
          <div className="row">
            <div className="col">
              <FormGroup className="mb-4">
                <Label htmlFor="name-input">{t("Room name")}</Label>
                <input
                  ref={inputName}
                  type="text"
                  className="form-control"
                  id="name-input"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder={t("Room name") + "..."}
                />
              </FormGroup>
              <FormGroup className="mb-4">
                <Label htmlFor="description-input">
                  {t("Room description")}
                </Label>
                <Input
                  type="text"
                  className="form-control"
                  id="description-input"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  placeholder={t("Room description") + "..."}
                />
              </FormGroup>
              <div className="row">
                <FormGroup className="mb-4 col-6">
                  <Label htmlFor="date-start-input">{t("Starting date")}</Label>
                  <Input
                    type="date"
                    className="form-control"
                    id="date-start-input"
                    min={today().date}
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                  />
                </FormGroup>
                <FormGroup className="mb-4 col-6">
                  <Label htmlFor="time-start-input">{t("Starting time")}</Label>
                  <Input
                    type="time"
                    className="form-control"
                    id="time-start-input"
                    value={startTime}
                    onChange={(e) => setTime(e.target.value)}
                  />
                </FormGroup>
              </div>
              <div className="row">
                <FormGroup className="mb-4 col-6">
                  <Label htmlFor="duration-input">{t("Duration")}</Label>
                  <Input
                    type="time"
                    className="form-control"
                    id="date-end-input"
                    value={duration}
                    onChange={(e) => setDuration(e.target.value)}
                  />
                </FormGroup>
                <FormGroup className="mb-4 col-12">
                  <div className="custom-control custom-checkbox">
                    <Input
                      type="checkbox"
                      className="custom-control-input"
                      onChange={(e) => {
                        setMdCheck(e.target.checked);
                      }}
                      id={"mdCheck"}
                      value={mdCheck ? 1 : 0}
                    />
                    <Label className="custom-control-label" htmlFor={"mdCheck"}>
                      {t("Wait for call administrator to start")}
                    </Label>
                  </div>
                </FormGroup>
              </div>
            </div>
            <div className="col">
              <FormGroup className="mb-4">
                <Label htmlFor="search-input">{t("Conference Members")}</Label>
                <Input
                  type="text"
                  className="form-control mb-2"
                  id="search-input"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  placeholder={t("Search users..")}
                />
                <div className="border">
                  <SimpleBar style={{ height: "320px" }}>
                    <div id="addContacts">
                      <SelectContact
                        handleCheckContact={handleCheckContact}
                        contacts={contacts}
                        search={search}
                        checkedContacts={selectedContact}
                      />
                    </div>
                  </SimpleBar>
                </div>
              </FormGroup>
            </div>
          </div>
          <hr />
          <h5 className="mb-3">{t("External participants")}</h5>
          <EmailList emailList={emailList} setEmailList={setEmailList} />
        </Form>
        <Alert isOpen={isOpenAlert} color="success">
          {message}
        </Alert>
      </ModalBody>
      <ModalFooter>
        <Button type="button" color="link" onClick={toggle}>
          {t("Close")}
        </Button>
        <Button type="button" color="primary" onClick={handleEditConference}>
          {t("Edit conference")}
        </Button>
      </ModalFooter>
    </Modal>
  );
};
