import React, {useState} from 'react'
import {
    DropdownMenu,
    DropdownItem,
    DropdownToggle,
    UncontrolledDropdown,
    UncontrolledTooltip,
    Button
} from "reactstrap";
import IChat from '../alias/IChat';
import IMessage from '../alias/IMessage';
import { ModalSelectUsers } from './ModalSelectUsers';
import { useTranslation } from "react-i18next";

interface IProps {
    activeChat: IChat;
    message: IMessage;
}


const ChatMessageMenuOption: React.FC<IProps> = ({ activeChat, message }) => {
    const { t } = useTranslation();
    const deleteMessage = (id: string) => {};
    const [showModal, setShowModal] = useState(false);
  
    const toggle = () => {
      setShowModal(!showModal);
    };
  

    if (!activeChat.isTyping) {
        return (
          <UncontrolledDropdown className="align-self-start">
            <DropdownToggle tag="a">
              <i className="ri-more-2-fill"></i>
            </DropdownToggle>
            <DropdownMenu>
              <DropdownItem>
                {t("Copy")}
                <i className="ri-file-copy-line float-right text-muted"></i>
              </DropdownItem>
              <DropdownItem>
                {t("Save")}{" "}
                <i className="ri-save-line float-right text-muted"></i>
              </DropdownItem>
                <DropdownItem>
                {/* <ModalSelectUsers show={showModal} toggle={toggle} />
                 Forward{" "}
                  <i className="ri-chat-forward-line float-right text-muted"></i> */}
                  <ModalSelectUsers show={showModal} toggle={toggle} />
                  <div className="user-chat-nav float-right">
                    <div id="create-group">
                      <Button /*botão de criação do grupo*/
                        onClick={toggle}
                        type="button"
                        color="link"
                        className="text-decoration-none text-muted font-size-18 py-0"
                      >
                        <i className="ri-group-line mr-1"></i>
                      </Button>
                    </div>
                    <UncontrolledTooltip target="create-group" placement="bottom">
                      Create group
                    </UncontrolledTooltip>
                  </div>
              </DropdownItem>
              <DropdownItem onClick={() => deleteMessage(message.id)}>
                Delete{" "}
                <i className="ri-delete-bin-line float-right text-muted"></i>
              </DropdownItem>
            </DropdownMenu>
          </UncontrolledDropdown>
        );
    }

    return <></>
}

export default ChatMessageMenuOption;
