import API from "@digivox/apicommunicator";
import { Settings } from "@digivox/apicommunicator/dist/types/types/types";

interface GetSettingsCallback {
  (settings: Settings): void;
}

export default class SettingsBusiness {
  private settings: Settings | undefined;
  constructor(private api: API) {}

  get(callback: GetSettingsCallback) {
    if (!this.api) return;
    if (this.settings) {
      callback(this.settings);
      return;
    }
    this.api.getSettings({}, (settings, error) => {
      if (error) throw error;
      if (settings) {
        this.settings = settings;
        callback(settings);
      }
    });
  }
}
