import React from "react";
import { Input, Label } from "reactstrap";
import IContact from "../alias/IContact";
import { useContact } from "../contexts/ContactContext";
import { SearchContact } from "../helpers/SearchContact";
import sortContactGroup from "../helpers/SortContactGroup";

interface IProps {
  search?: string;
  contacts: IContact[];
  handleCheckContact(selected: boolean, contact: IContact): void;
}

const SelectContact: React.FC<IProps> = ({
  search,
  contacts,
  handleCheckContact,
}) => {
  const { checkedContacts, setCheckedContacts } = useContact();

  return (
    <div className="mt-2">
    {sortContactGroup(contacts).map(
        (contact, key) => (
          <div key={key}>
            <div className="ml-3 pl-1 font-weight-bold text-primary">
              {contact.group}
            </div>

            <ul className="list-unstyled contact-list mb-2">
              {contact.children.map((child, keyChild) => (
                <li key={keyChild}>
                  <div className="custom-control custom-checkbox">
                    <Input
                      type="checkbox"
                      className="custom-control-input"
                      onChange={(e) => {
                        handleCheckContact(e.target.checked, child);
                        if(e.target.checked === true){
                          setCheckedContacts([...checkedContacts, child]);
                        }
                      }}
                      id={"memberCheck" + child.id}
                    />
                    <Label
                      className="custom-control-label"
                      htmlFor={"memberCheck" + child.id}
                    >
                      {child.name}
                    </Label>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )
      )}
    </div>
  );
};

export default SelectContact;
