import React from "react";
import IMessage from "../alias/IMessage";
interface ReplyMessageWrapper {
  message: IMessage;
}
const ReplyMessageWrapper: React.FC<ReplyMessageWrapper> = ({ message }) => {
  if (!message.isReplyMessage) return <></>;
  let treeDots = "";
  if (message.replyedMessage && message.replyedMessage.length > 50)
    treeDots = "...";

  return (
    <div
      style={{ color: "#343a40", marginBottom: "5px", borderRadius: 16 }}
      className={`bg-light pt-1 pb-1 pr-3 pl-3`}
    >
      <div>
        <strong>{message.replyedMessageTo}</strong>
      </div>
      <div>
        <small className="text-muted">
          {message.replyedMessage?.slice(0, 50) + treeDots}
        </small>
      </div>
    </div>
  );
};

export default ReplyMessageWrapper;
