import Chat from "./chat/chatcommunicator";
import { Events, FileMessage, Message, Presence } from "./chat/types/types";
import IMessage from "../alias/IMessage";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import { SendMessageCallback, SubscriptionCallBack } from "./types";
import IChat from "../alias/IChat";
import ChatType from "../enuns/ChatType";
import { ChatType as XMPPChatType } from "./types";
import SettingsBusiness from "./business/Settings";
import short from "short-uuid";
import ContactsBusiness from "./business/Contacts";
import GroupsBusiness from "./business/Groups";
import { isUrl } from "./utils/parseUtils";
import { getFileUrlStr } from "./utils/formatUtils";
import UserBusiness from "./business/User";
import { getFrom } from "./chat/util/stanzaUtils";

export default interface SquadChatCommunicator {
  chat: Chat;
  nextSubscriptionId: number;
  subscriptions: Map<number, SubscriptionCallBack>;
}
export default class SquadChatCommunicator {
  constructor(
    private settings: SettingsBusiness,
    private contacts: ContactsBusiness,
    private groups: GroupsBusiness,
    private user: UserBusiness,
    subscribeFunction: SubscriptionCallBack
  ) {
    this.nextSubscriptionId = 0;
    this.subscriptions = new Map<number, SubscriptionCallBack>();
    this.makeChat(subscribeFunction);
  }
  makeChat(subscribeFunction: SubscriptionCallBack) {
    this.subscribe(subscribeFunction);
    this.settings.get((settings) => {
      const options = {
        service: `wss://${settings.xmppDomain}:5280/websocket`,
        domain: settings.xmppDomain,
        username: settings.xmppUser,
        password: settings.xmppPw,
        resource: `squad.web_${settings.xmppUser}_${short().generate()}`,
      };
      const chat = new Chat(options);
      this.chat = chat;
      this.subscribeChatEvents(chat);
      chat.client.start();
    });
  }
  subscribeChatEvents(chat: Chat) {
    chat.on(Events.MESSAGE, this.onMessage);
    chat.on(Events.PRESENCE, this.onPresence);
    chat.on(Events.ONLINE, () => this.notify(Events.ONLINE, null));
    chat.on(Events.OFFLINE, () => this.notify(Events.OFFLINE, null));
    chat.on(Events.ERROR, this.onError);
    chat.on(Events.STANZA, this.onStanza);
    chat.on(Events.RECEIVED, (data) => this.notify(Events.RECEIVED, data));
    chat.on(Events.DISPLAYED, (data) => this.notify(Events.DISPLAYED, data));
    chat.on(Events.COMPOSING, (data) => this.notify(Events.COMPOSING, data));
    chat.on(Events.ACTIVE, (data) => this.notify(Events.ACTIVE, data));
    chat.on(Events.GROUP_EVENT, this.onGroupEvent);
    chat.on(Events.REMOVE_MEMBER, this.onRemovingMember);
  }
  onGroupEvent = (event: Message) => {
    this.settings.get((settings) => {
      const isSentByMe =
        event.from.split("/")[1] === settings.xmppUser ||
        event.from.split("@")[0] === settings.xmppUser;
      const eventParsed: IMessage = {
        id: event.id,
        userName: this.getMessageUserName(event) || "",
        userProfilePicture:
          this.getMessageUserProfilePicture(event) || undefined,
        message: "",
        time: event.sent_at,
        isFileMessage: false,
        isImageMessage: false,
        isVideoMessage: false,
        isAudioMessage: false,
        fileUrl: "",
        toUser:
          isSentByMe && event.from.includes("@conference.")
            ? event.from.split("/")[0]
            : event.to,
        fromUser: isSentByMe ? settings.xmppUser : event.from.split("/")[0],
        deliverStatus: DeliverStatus.DELIVERED,
        readStatus: ReadStatus.UNREAD,
        deliverTimestamp: new Date().toISOString(),
        readTimestamp: "",
        content: "",
        contentType: (event as FileMessage).fileUrl || "text",
        isSentByMe: isSentByMe,
        isReplyMessage: false,
        isForwardMessage: false,
        replyedMessage: "",
        replyedMessageTo: "",
        replyedMessageId: "",
        eventId: event.eventId,
        eventBody: event.eventBody,
      };
      this.notify(Events.GROUP_EVENT, eventParsed);
    });
  };
  onMessage = (msg: Message | FileMessage) => {
    this.settings.get((settings) => {
      const fileParams = {
        isFile: "fileUrl" in msg ? true : false,
        isImage: false,
        isVideo: false,
        isAudio: false,
      };
      if (fileParams.isFile) {
        fileParams.isImage = /\.(jpe?g|png|gif|ico)$/i.test(
          (msg as FileMessage).fileUrl
        );
        fileParams.isVideo = /\.(mp4|avi|mov)$/i.test(
          (msg as FileMessage).fileUrl
        );
        fileParams.isAudio = /\.(ogg|mp3|wav|m4a|webm)$/i.test(
          (msg as FileMessage).fileUrl
        );
      }
      if (isUrl(msg.message)) {
        const splittedMsg = msg.message.split("/");
        const extension = splittedMsg[splittedMsg.length - 1];
        if (extension.includes(".") && splittedMsg.length > 1) {
          fileParams.isFile = true;
          fileParams.isImage = /\.(jpe?g|png|gif|ico)$/i.test(msg.message);
          fileParams.isVideo = /\.(mp4|avi|mov)$/i.test(msg.message);
          fileParams.isAudio = /\.(ogg|mp3|wav|m4a|webm)$/i.test(msg.message);
        }
      }
      const isSentByMe =
        msg.from.split("/")[1] === settings.xmppUser ||
        msg.from.split("@")[0] === settings.xmppUser;
      const message: IMessage = {
        id: msg.id,
        userName: this.getMessageUserName(msg) || "",
        userProfilePicture: this.getMessageUserProfilePicture(msg) || undefined,
        message: msg.message || (msg as FileMessage).fileUrl,
        time: msg.sent_at,
        isFileMessage:
          fileParams.isFile &&
          !fileParams.isImage &&
          !fileParams.isVideo &&
          !fileParams.isAudio,
        isImageMessage: fileParams.isImage,
        isVideoMessage: fileParams.isVideo,
        isAudioMessage: fileParams.isAudio,
        fileUrl: getFileUrlStr(fileParams, msg),
        toUser:
          isSentByMe && msg.from.includes("@conference.")
            ? msg.from.split("/")[0]
            : msg.to,
        fromUser: isSentByMe ? settings.xmppUser : msg.from.split("/")[0],
        deliverStatus: DeliverStatus.DELIVERED,
        readStatus: ReadStatus.UNREAD,
        deliverTimestamp: new Date().toISOString(),
        readTimestamp: "",
        content: JSON.stringify({
          reply_msg: msg.reply_msg,
          reply_msg_id: msg.reply_msg_id,
          reply_to: msg.reply_to,
        }),
        contentType: (msg as FileMessage).fileUrl || "text",
        isSentByMe: isSentByMe,
        isReplyMessage: !!msg.reply_msg,
        isForwardMessage: !!msg.reply_to && !msg.reply_msg,
        replyedMessage: msg.reply_msg,
        replyedMessageTo:
          this.contacts.getContacts().get(msg.reply_to?.split("@")[0] || "")
            ?.name || this.user.user?.id === msg.reply_to?.split("@")[0]
            ? this.user.user?.name
            : "Participant",
        replyedMessageId: msg.reply_msg_id,
      };
      this.notify(Events.MESSAGE, message);
    });
  };
  onPresence = (presence: Presence) => {
    this.notify(Events.PRESENCE, presence);
  };
  onStanza(stanza: any) {}
  onError(error: any) {}
  forwardMessage = (
    chat: IChat,
    message: IMessage,
    callback: SendMessageCallback
  ) => {
    this.chat.forwardMsg(
      chat.jid,
      chat.jid.includes("@conference.")
        ? XMPPChatType.GROUPCHAT
        : XMPPChatType.CHAT,
      message.fromUser,
      message.message,
      message.id,
      callback
    );
  };
  replyMsg = (
    chat: IChat,
    message: IMessage,
    replyedMessage: IMessage,
    cb: SendMessageCallback
  ) => {
    this.chat.replyMsg(
      chat.jid,
      chat.jid.includes("@conference.")
        ? XMPPChatType.GROUPCHAT
        : XMPPChatType.CHAT,
      message.message,
      replyedMessage.fromUser,
      replyedMessage.message,
      replyedMessage.id,
      cb
    );
  };
  sendMessage = (chat: IChat, text: string, callback: SendMessageCallback) => {
    this.chat.sendMessage(
      chat.jid,
      chat.chatType === ChatType.USER
        ? XMPPChatType.CHAT
        : XMPPChatType.GROUPCHAT,
      text,
      callback
    );
  };
  sendFiles = (chat: IChat, files: File[], cb: any) => {
    files.forEach((file) => {
      this.chat.sendFile(
        chat.jid,
        chat.jid.includes("@conference.")
          ? XMPPChatType.GROUPCHAT
          : XMPPChatType.CHAT,
        file,
        cb
      );
    });
  };
  joinGroup(to: string) {
    this.chat.joinRoom(to);
  }
  joinGroups(groups: string[]) {
    groups.forEach((jid) => this.joinGroup(jid));
  }
  changeStatus(status: string) {
    this.chat.sendPresence(status);
  }
  getMessageUserName = (msg: Message | FileMessage) => {
    if (msg.from.includes("@conference.")) {
      const groups = this.groups.getGroups();
      const group = groups.get(msg.from.split("@")[0]);
      const member = group?.members.filter(
        (member) => member.userId === msg.from.split("/")[1]
      )[0];
      return member?.name;
    } else {
      return this.contacts.getContacts().get(msg.from.split("@")[0])?.name;
    }
  };
  getMessageUserProfilePicture = (msg: Message | FileMessage) => {
    if (msg.from.includes("@conference.")) {
      const groups = this.groups.getGroups();
      const group = groups.get(msg.from.split("@")[0]);
      const member = group?.members.filter(
        (member) => member.userId === msg.from.split("/")[1]
      )[0];
      return member?.profilePicture;
    } else {
      return this.contacts.getContacts().get(msg.from.split("@")[0])
        ?.profilePicture;
    }
  };
  subscribe(subscribeCallback: SubscriptionCallBack) {
    this.subscriptions.set(this.nextSubscriptionId, subscribeCallback);
    this.nextSubscriptionId += 1;
  }
  removeSubscription(id: number) {
    this.subscriptions.delete(id);
  }
  removeAllSubscription() {
    this.subscriptions = new Map<number, SubscriptionCallBack>();
  }
  notify(event: string, data: any) {
    if (this.subscriptions) {
      this.subscriptions.forEach((subscribeCallback) => {
        if (subscribeCallback) subscribeCallback(event, data);
      });
    }
  }
}
