  function chatCommunicatorSubscribe(evt: string, data: any) {
    switch (evt) {
      case Events.RECONNECTED:
        setDisconnected(false);
      // eslint-disable-next-line
      case Events.ONLINE:
        squadService?.chats.list((receivedChats) => {
          const chats: IChat[] = [];
          receivedChats.forEach((cht) => {
            if (!chats.find((chat) => chat.id === cht.id)) {
              chats.push(cht);
            }
          });
          setChats(chats);
          //populating groups
          squadService?.groups.list((groups) => {
            setGroups(groups);
            //Joining Groups Rooms in XMPP
            squadService?.chat?.joinGroups(groups.map((group) => group.jid));
          });
          //populating contacts
          squadService?.contacts.list((contacts) => {
            setContacts(contacts);
          });

          //populating conferences
          setListRooms(true);
          setFlagToLoadFavoriteMessages({ contactType: "" });
        });
        break;
      case Events.ERROR:
      case Events.OFFLINE:
        if (localStorage.getItem("logout") !== "true") {
          squadService?.chat?.makeChat(chatCommunicatorSubscribe);
        }
        localStorage.removeItem("logout");
        console.log(evt);
        break;
      case Events.RECONNECTING:
        // setShowChat(false);
        // setActiveChat(actChtData);
        // setChats([]);
        // setGroups([]);
        // setContacts([]);
        // setConferenceList([]);
        console.log("Reconnecting");
        setDisconnected(true);
        break;
      case Events.MESSAGE:
        if (data.fromUser === user.jid) break;
        let find = false;

        let newChats: IChat[] = [];
        chats.forEach((chat) => {
          if (
            data.fromUser.split("@")[0] === chat.jid.split("@")[0] ||
            data.toUser.split("@")[0] === chat.jid.split("@")[0]
          ) {
            find = true;
            if (!chat.messages.filter((msg) => msg.id === data.id)[0])
              chat.messages.push(data);
            if (chat.id === activeChat.id) {
              let chatTemporary = { ...chat };
              setActiveChat(chatTemporary);
              setReadMessagePayload({
                authUser: user.jid as string,
                between: activeChat.jid,
                chatType: activeChat.chatType,
                id: activeChat.messages[activeChat.messages.length - 1].id,
              });
            } else {
              chat.unRead++;
              chat.mencioned += ParseUserTag(user.id, data.message);
              showNotification(chat, data);
            }
            newChats.unshift(chat);
          } else {
            newChats.push(chat);
          }
        });

        if (!find) {
          let newChat: IChat = {} as IChat;
          squadService?.contacts.listAllUsers((listContacts) => {
            listContacts.forEach((c) => {
              if (
                data.fromUser.split("@")[0] === c.jid.split("@")[0] ||
                data.toUser.split("@")[0] === c.jid.split("@")[0]
              ) {
                if (c.jid === data.fromUser) {
                  find = true;
                  newChat = {
                    id: c.id,
                    jid: c.jid,
                    name: c.name,
                    profilePicture: c.profilePicture,
                    status: c.status,
                    unRead: 1,
                    chatType: ChatType.USER,
                    inputMessage: "",
                    isTyping: false,
                    messages: [],
                    favorite: c.favorite,
                    added: true,
                    mencioned: 0,
                  };
                  newChat.messages.push(data);
                  newChats.unshift(newChat);
                  if (!c.added) {
                    squadService?.contacts.add(newChat, (s) => {
                      if (s) {
                        squadService?.contacts.list((ctcs) => {
                          setContacts(ctcs);
                        });
                      }
                    });
                  }
                }
              }
            });
          });
        }

        if (!find) {
          let newChat: IChat = {} as IChat;

          squadService?.groups.list((listGroups) => {
            listGroups.forEach((g) => {
              if (
                data.fromUser.split("@")[0] === g.jid.split("@")[0] ||
                data.toUser.split("@")[0] === g.jid.split("@")[0]
              ) {
                if (g.jid === data.fromUser) {
                  find = true;
                  newChat = {
                    id: g.groupId,
                    jid: g.jid,
                    name: g.name,
                    profilePicture: g.profilePicture,
                    attachedFileList: undefined,
                    conferenceRoomParticipants: undefined,
                    department: undefined,
                    email: undefined,
                    groupId: g.groupId,
                    members: g.members,
                    messageLoaded: undefined,
                    userDescription: undefined,
                    status: undefined,
                    unRead: 1,
                    chatType: ChatType.GROUP,
                    inputMessage: "",
                    isTyping: false,
                    messages: [],
                    favorite: g.favorite,
                    added: g.added,
                    mencioned: 0,
                  };
                  newChat.messages.push(data);
                  newChats.unshift(newChat);
                }
              }
            });
          });
        }

        setChats(newChats);

        break;
      case Events.RECEIVED:
        setChats(
          chats.map((chat) => {
            if (chat.messages.find((msg) => msg.id === data.id))
              chat.messages = chat.messages.map((message) => {
                message.deliverStatus = DeliverStatus.DELIVERED;
                return message;
              });
            return chat;
          })
        );
        break;
      case Events.DISPLAYED:
        setChats(
          chats.map((chat) => {
            if (chat.messages.find((msg) => msg.id === data.id))
              chat.messages = chat.messages.map((message) => {
                message.readStatus = ReadStatus.READ;
                return message;
              });
            return chat;
          })
        );
        break;
      case Events.COMPOSING:
        setChats(
          chats.map((chat) => {
            if (chat.jid === data) {
              chat.isTyping = true;
              if (activeChat.jid === data) setActiveChat(chat);
              deleteTypingTimeout(chat.jid);
              setTypingTimeout(chat.jid);
            }
            return chat;
          })
        );
        break;
      case Events.ACTIVE:
        setChats(
          chats.map((chat) => {
            if (chat.jid === data) {
              chat.isTyping = false;
              if (activeChat.jid === data) {
                let chatUpdate = { ...chat };
                setActiveChat(chatUpdate);
              }
              deleteTypingTimeout(chat.jid);
            }
            return chat;
          })
        );
        break;
      case Events.PRESENCE:
        setChats(
          chats.map((cht) => {
            if (cht.jid && data.from) {
              if (
                cht.jid === data.from.split("/")[0] &&
                !cht.jid.includes("@conference.")
              ) {
                cht.status = data.status;
              }
            }
            return cht;
          })
        );
        setContacts(
          contacts.map((ctc) => {
            if (ctc.jid && data.from) {
              if (
                ctc.jid === data.from.split("/")[0] &&
                !ctc.jid.includes("@conference.")
              ) {
                ctc.status = data.status;
              }
            }
            return ctc;
          })
        );
        break;

      case Events.SEND_EVENT:
        const event = data as IMessage;
        let finded;
        let newChat: IChat[] = [];
        chats.forEach((chat) => {
          if (
            event.fromUser.split("@")[0] === chat.jid.split("@")[0] ||
            event.toUser.split("@")[0] === chat.jid.split("@")[0]
          ) {
            finded = true;
            if (!chat.messages.filter((msg) => msg.id === event.id)[0])
              chat.messages.push(event);
            if (chat.id === activeChat.id) {
              let chatTemporary = { ...chat };
              setActiveChat(chatTemporary);
              setReadMessagePayload({
                authUser: user.jid as string,
                between: activeChat.jid,
                chatType: activeChat.chatType,
                id: chat.messages.filter(
                  (msg: IMessage) => msg.id === data.id
                )[0].id,
              });
            } else {
              chat.unRead++;
              chat.mencioned += ParseUserTag(user.id, data.message);
              showNotification(chat, data);
            }
            newChat.unshift(chat);
          } else {
            newChat.push(chat);
          }
        });
        if (!finded) {
          groups.forEach((grp) => {
            if (
              event.fromUser.split("@")[0] === grp.jid.split("@")[0] ||
              event.toUser.split("@")[0] === grp.jid.split("@")[0]
            ) {
              finded = true;
              let groupToBeInserted: IChat = {
                added: grp.added,
                chatType: ChatType.GROUP,
                favorite: grp.favorite,
                id: grp.groupId,
                inputMessage: "",
                isTyping: false,
                jid: grp.jid,
                messages: [event],
                name: grp.name,
                unRead: 0,
                attachedFileList: undefined,
                department: undefined,
                email: undefined,
                groupId: grp.groupId,
                members: grp.members,
                messageLoaded: undefined,
                profilePicture: grp.profilePicture,
                status: undefined,
                userDescription: grp.description,
                mencioned: 0,
              };
              newChat.unshift(groupToBeInserted);
              setActiveChat(groupToBeInserted);
            }
          });
        }
        if (!finded) {
          squadService?.groups.list((grps) => {
            setGroups(grps);
            grps.forEach((grp) => {
              if (
                event.fromUser.split("@")[0] === grp.jid.split("@")[0] ||
                event.toUser.split("@")[0] === grp.jid.split("@")[0]
              ) {
                finded = true;
                let groupToBeInserted: IChat = {
                  added: grp.added,
                  chatType: ChatType.GROUP,
                  favorite: grp.favorite,
                  id: grp.groupId,
                  inputMessage: "",
                  isTyping: false,
                  jid: grp.jid,
                  messages: [event],
                  name: grp.name,
                  unRead: 1,
                  attachedFileList: undefined,
                  department: undefined,
                  email: undefined,
                  groupId: grp.groupId,
                  members: grp.members,
                  messageLoaded: undefined,
                  profilePicture: grp.profilePicture,
                  status: undefined,
                  userDescription: grp.description,
                  mencioned: 0,
                };
                newChat.unshift(groupToBeInserted);
              }
            });
          });
        }
        setChats(newChat);
        if (event.eventId === 6 || event.eventId === 2) {
          refreshMembers(event);
        }
        break;
      default:
        console.log(evt);
        break;
    }
  }

  const refreshMembers = (event: IMessage) => {
    squadService?.groups.list((grps) => {
      setGroups(grps);
      setChats(
        chats.map((chat) => {
          if (
            event.fromUser.split("@")[0] === chat.jid.split("@")[0] ||
            event.toUser.split("@")[0] === chat.jid.split("@")[0]
          ) {
            chat.members = grps.find((grp) => grp.groupId === chat.id)?.members;
            if (chat.id === activeChat.id) {
              setActiveChat(chat);
            }
          }
          return chat;
        })
      );
    });
  };

  const deleteTypingTimeout = (jid: string) => {
    clearTimeout(typingTimeout.get(jid)?.timeout || setTimeout(() => {}, 1));
    typingTimeout.delete(jid);
  };
  const setTypingTimeout = (jid: string) => {
    const timeoutData: TypingTimeoutData = {
      msgStr: "",
      timeout: setTimeout(() => {
        setChats(
          chats.map((cht) => {
            if (cht.jid === jid) {
              cht.isTyping = false;
              if (activeChat.jid === jid) setActiveChat(cht);
            }
            return cht;
          })
        );
      }, 20000),
    };
    typingTimeout.set(jid, timeoutData);
  };

  squadService?.updateChatSubscribeFuncition(chatCommunicatorSubscribe);
  // Comentado para evitar warnings no console enquanto no  usado
  // function createGroup(group: INewGroup, callback: CreateGroupCallback) {
  //   squadService?.groups.create(group, (iGroup: IGroup) => {
  //     squadService?.groups.list((groups) => {
  //       setGroups(groups);
  //       callback(true);
  //     });
  //   });
  // }

  function sendMessage(
    chat: IChat,
    message: string,
    callback: SendMessageCallback
  ) {
    squadService?.chat?.chat.sendMessage(
      chat.jid,
      chat.jid.includes("@conference.")
        ? XMPPChatType.GROUPCHAT
        : XMPPChatType.CHAT,
      message,
      callback
    );
  }

  useEffect(init, []);

  return <></>;
};

export default SquadCommunicator;
