import API from "@digivox/apicommunicator";
import IContact from "../../alias/IContact";
import IChat from "../../alias/IChat";
import IGroup from "../../alias/IGroup";
import { parseContact } from "../utils/parseUtils";
import SettingsBusiness from "./Settings";
import {
  AddContactCallback,
  FavoriteUserCallback,
} from "@digivox/apicommunicator/dist/types/types/types";
import debounce from "lodash/debounce";

interface ContactsCallBack {
  (contacts: IContact[]): void;
}

export default interface ContactsBusiness {}
export default class ContactsBusiness {
  private contacts: Map<string, IContact>;
  constructor(private api: API, private settings: SettingsBusiness) {
    this.contacts = new Map<string, IContact>();
  }
  getContacts() {
    return this.contacts;
  }

  getContactsOutsideUserLists(callback: ContactsCallBack){
    this.settings.get((settings) => {
      this.api.getContacts(
        {
          paramsUrl: {
            limit: 5000,
            contactType: "contact",
            domain: settings.sipDomain,
            added: false
          },
        }, (apiContacts: any, err: any) => {
          if (!err) {
            if (!this.api) return;
            const searchList = apiContacts.contacts.map((contact: any) => {
              return parseContact(contact);
            });
            callback(searchList);
        }
      });
    })
  };


  list(callback: ContactsCallBack) {
    this.settings.get((settings) => {
      this.api.getContacts(
        {
          paramsUrl: {
            added: true,
            limit: 5000,
            contactType: "contact",
            domain: settings.sipDomain,
          },
        },
        (apiContacts: any, err: any) => {
          // Populando o Map de contatos
          if (!err) {
            if (!this.api) return;
            this.populate(apiContacts);

            //Favoritar o contato dentro da população com
            //favoriteCallback()

            callback(Array.from(this.contacts.values()));
          }
        }
      );
    });
  }
  populate(apiContacts: any) {
    apiContacts.contacts.forEach((contact: any) => {
      this.contacts.set(contact.id, parseContact(contact));
    });
  }
  setFavorite(
    favorited: IGroup | IChat | IContact,
    callback: FavoriteUserCallback
  ) {
    this.api.setFavoriteUser(
      {
        contactID: favorited.jid.split("@")[0],
        contactType: favorited.jid.includes("@conference.")
          ? "group"
          : "contact",
        favorite: !favorited.favorite,
      },
      callback
    );
  }
  add = (ctc: IContact, callback: AddContactCallback) => {
    this.api.addContact(
      {
        contactType: "contact",
        contactId: ctc.id,
        favorite: false,
        blocked: false,
      },
      callback
    );
  };
  search = debounce((strSearch: string, callback: ContactsCallBack) => {
    this.settings.get((settings) => {
      this.api.getContacts(
        {
          paramsUrl: {
            limit: 5000,
            contactType: "contact",
            domain: settings.sipDomain,
            name: strSearch,
          },
        },
        (apiContacts: any, err: any) => {
          if (!err) {
            if (!this.api) return;
            const searchList = apiContacts.contacts.map((contact: any) => {
              return parseContact(contact);
            });

            callback(searchList);
          }
        }
      );
    });
  }, 250);
}
