import { useModal } from "./../contexts/ModalContext";
import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import {
  FormGroup,
  Input,
  Modal,
  ModalHeader,
} from "reactstrap";
import SimpleBar from "simplebar-react";
import IContact from "../alias/IContact";
import { useContact } from "../contexts/ContactContext";
import SelectContact from "./SelectContact";

const ModalAddParticipant = () => {
  const handleCheckContact = (selected: boolean, contact: IContact) => {
    if (selected) {
      setSelectedContact([...selectedContact, contact]);
    } else {
      setSelectedContact(
        selectedContact.filter((sel) => {
          if (sel.id === contact.id) {
            return false;
          }
          return true;
        })
      );
    }
  };
  const { t } = useTranslation();
  const { contacts } = useContact();
  const [search, setSearch] = useState("");
  const [selectedContact, setSelectedContact] = useState<IContact[]>([]);
  const { showModalAddParticipant, setShowModalAddParticipant} = useModal();
  const toggle = () => {
    setShowModalAddParticipant(!showModalAddParticipant);
  }
  return(
    <Modal
      backdrop="static"
      isOpen={!!showModalAddParticipant}
      centered
      toggle={toggle}
    >
      <ModalHeader toggle={toggle}>{t("Add Member")}</ModalHeader>
      <div className="col">
        <FormGroup className="mb-4">
          <Input
            type="text"
            className="form-control mt-2"
            id="search-input"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            placeholder={t("Search users..")}
          />
          <div className="border">
            <SimpleBar style={{ height: "320px" }}>
              <div id="addContacts">
                <SelectContact
                  handleCheckContact={handleCheckContact}
                  contacts={contacts}
                  search={search}
                  checkedContacts={selectedContact}
                />
              </div>
            </SimpleBar>
          </div>
        </FormGroup>
        <div className="d-flex justify-content-center mt-3">
          <button
            className="btn btn-success btn-block"
            onClick={() => setShowModalAddParticipant(true)}
          > 
            
            <div style={{display: "flex", flexDirection: "row", justifyContent: 'center', alignItems: 'center'}}>
              <i className="ri-add-line mr-1"/>
              {t("Add participants")}
            </div>
          </button>
        </div>
    </div>
    </Modal>
  );
}

export default ModalAddParticipant;
