import React, { useState } from "react";
import { Media } from "reactstrap";
import { Link } from "react-router-dom";

//simplebar
import SimpleBar from "simplebar-react";

import CallHistoryDates from "./CallHistoryDates";
import ICallHistory from "../alias/ICallHistory";
import DirectionType from "../enuns/DirectionType";
import StatusHistoryType from "../enuns/StatusHistoryType";
import ModalDetailsCallHistory from "./ModalDetailsCallHistory";

interface IProps {
  recentCallHistory: ICallHistory[];
}

const CallHistoryList: React.FC<IProps> = ({ recentCallHistory }) => {
  const [
    callHistorySelected,
    setCallHistorySelected,
  ] = useState<ICallHistory | null>(null);

  const statusColor = (call: ICallHistory): string => {
    if (call.direction === DirectionType.INBOUND) {
      if (call.status === StatusHistoryType.SUCCESS) {
        return "green";
      }
      return "red";
    }

    if (call.status !== StatusHistoryType.SUCCESS) {
      return "red";
    } else {
      return "green";
    }
  };

  const statusIcon = (call: ICallHistory) => {
    if (call.direction === DirectionType.INBOUND) {
      return <i className="ri-arrow-right-down-line align-middle" />;
    }
    return <i className="ri-arrow-right-up-line align-middle" />;
  };

  return (
    <>
      <ModalDetailsCallHistory
        callHistory={callHistorySelected}
        setCallHistory={setCallHistorySelected}
      />
      <SimpleBar style={{ maxHeight: "100%" }} className="chat-message-list">
        <ul className="list-unstyled chat-list chat-user-list" id="chat-list">
          {recentCallHistory.map((call, key) => (
            <li key={key} id={"call" + key} className={``}>
              <Link
                to="#"
                onClick={() => setCallHistorySelected(call)}
                className="call-history-name-or-number"
              >
                <Media>
                  <div className={"chat-user-img align-self-center mr-3"}>
                    <div className="avatar-xs">
                      <span
                        className="avatar-title rounded-circle bg-soft-primary"
                        style={{ color: statusColor(call) }}
                      >
                        {statusIcon(call)}
                      </span>
                    </div>
                  </div>
                  <Media body className="overflow-hidden">
                    <h5 className="text-truncate font-size-15 mb-1">
                      <div className="name">
                        {call.name ? call.name : call.number}
                      </div>
                      <div className="number">{call.number}</div>
                    </h5>
                    <div className="chat-user-message text-truncate mb-0">
                      <CallHistoryDates call={call} />
                    </div>
                  </Media>
                  {call.duration && (
                    <div className="font-size-11">{call.duration}</div>
                  )}
                </Media>
              </Link>
            </li>
          ))}
        </ul>
      </SimpleBar>
    </>
  );
};

export default CallHistoryList;
