import React, { useRef, useState } from "react";
import {
  Dropdown,
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  Card,
  Button,
  InputGroup,
  InputGroupAddon,
  Input,
  Tooltip,
} from "reactstrap";

import SimpleBar from "simplebar-react";

//Import components
import CustomCollapse from "./CustomCollapse";

//Import Images
import avatar1 from "./../assets/images/users/avatar-1.jpg";

//i18n
import { useTranslation } from "react-i18next";
import { useAuth } from "../contexts/AuthContext";
import { ModalChangePassword } from "./ModalChangePassword";
import { isExtensionLine } from './../services/utils/parseUtils';
import { useModal } from "./../contexts/ModalContext";
import ModalEditInfo from "./ModalEditInfo";
import { getUserStatusName } from "../helpers/StatusUtils";
import ModalConfigDevices from "./ModalConfigDevices";
import Swal from "sweetalert2";

function SettingTab() {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [isOpen1, setIsOpen1] = useState(true);
  const [isOpen2, setIsOpen2] = useState(false);
  const [isOpen3, setIsOpen3] = useState(false);

  const [busy, setBusy] = useState("");
  const [noAnswer, setNoAnswer] = useState("");
  const [notRegistered, setNotRegistered] = useState("");
  const [followMeAlways, setFollowMeAlways] = useState("");

  const [lockedBusyInput, setLockedBusyInput] = useState(true);
  const [lockedNoAnswerInput, setLockedNoAnswerInput] = useState(true);
  const [lockedNotRegisteredInput, setLockedNotRegisteredInput] = useState(
    true
  );
  const [lockedFollowMeAlwaysInput, setLockedFollowMeAlwaysInput] = useState(
    true
  );

  const [display1, setDisplay1] = useState(false);
  const [display2, setDisplay2] = useState(false);
  const [display3, setDisplay3] = useState(false);
  const [display4, setDisplay4] = useState(false);

  const { user, setUser } = useAuth();
  const { t } = useTranslation();
  const {
    setShowModalEditInfo,
    setShowModalChangePassword,
    setShowModalDevicesConfig,
  } = useModal();

  const circleColor = () => {
    if (user.status === "online") return "text-success";
    else if (user.status === "away") return "text-warning";
    else return "text-danger";
  };

  const toggleCollapse1 = () => {
    setIsOpen1(!isOpen1);
    setIsOpen2(false);
    setIsOpen3(false);
  };
  const toggleCollapse2 = () => {
    setIsOpen1(false);
    setIsOpen2(!isOpen2);
    setIsOpen3(false);
  };
  const toggleCollapse3 = () => {
    setIsOpen1(false);
    setIsOpen2(false);
    setIsOpen3(!isOpen3);
  };

  const toggle = () => setDropdownOpen(!dropdownOpen);

  const setUserStatus = (status: string) => {
    setUser({ ...user, status });
  };

  const [tooltipOpen1, setTooltipOpen1] = useState(false);
  const [tooltipOpen2, setTooltipOpen2] = useState(false);
  const [tooltipOpen3, setTooltipOpen3] = useState(false);
  const [tooltipOpen4, setTooltipOpen4] = useState(false);

  const toggleTooltip1 = () => setTooltipOpen1(!tooltipOpen1);
  const toggleTooltip2 = () => setTooltipOpen2(!tooltipOpen2);
  const toggleTooltip3 = () => setTooltipOpen3(!tooltipOpen3);
  const toggleTooltip4 = () => setTooltipOpen4(!tooltipOpen4);

  /* const isThisStringNotEmpty = (value: string) => {
    if(value !== ""){
      return true;
    }else{
      return false
    }
  } */

  const clearInputs = () => {
    setBusy("");
    setNoAnswer("");
    setNotRegistered("");
    setFollowMeAlways("");
  };

  const lockAllInputs = () => {
    setLockedBusyInput(true);
    setLockedNoAnswerInput(true);
    setLockedNotRegisteredInput(true);
    setLockedFollowMeAlwaysInput(true);
  };

  const isEveryInputLocked = () => {
    if (
      lockedBusyInput === true &&
      lockedNoAnswerInput === true &&
      lockedNotRegisteredInput === true &&
      lockedFollowMeAlwaysInput === true
    ) {
      return true;
    } else {
      return false;
    }
  };

  const isEveryDataEmpty = () => {
    if (
      busy === "" &&
      noAnswer === "" &&
      notRegistered === "" &&
      followMeAlways === ""
    ) {
      return true;
    } else {
      return false;
    }
  };

  return (
    <React.Fragment>
      <ModalChangePassword />
      <div>
        <div className="px-4 pt-4">
          <h4 className="mb-0">{t("Settings")}</h4>
        </div>

        <div className="text-center border-bottom p-4">
          <div className="mb-4 profile-user">
            <img
              src={user.profilePicture || avatar1}
              className="rounded-circle avatar-lg img-thumbnail"
              alt="chatvia"
            />
            <Button
              type="button"
              color="light"
              className="avatar-xs p-0 rounded-circle profile-photo-edit"
              onClick={() => setShowModalEditInfo(true)}
            >
              <i className="ri-pencil-fill"></i>
            </Button>
          </div>
          <h5 className="font-size-16 mb-1 text-truncate">{user.name}</h5>
          <Dropdown
            isOpen={dropdownOpen}
            toggle={toggle}
            className="d-inline-block mb-1"
          >
            <DropdownToggle tag="a" className="text-muted pb-1 d-block">
              <i
                className={`ri-record-circle-fill font-size-10 ${circleColor()} mr-1 d-inline-block`}
              ></i>{" "}
              {t(getUserStatusName(user.status))}{" "}
              <i className="mdi mdi-chevron-down"></i>
            </DropdownToggle>

            <DropdownMenu>
              <DropdownItem
                onClick={() => {
                  setUserStatus("online");
                }}
              >
                {t("Online")}
              </DropdownItem>
              <DropdownItem
                onClick={() => {
                  setUserStatus("away");
                }}
              >
                {t("Away")}
              </DropdownItem>
              <DropdownItem
                onClick={() => {
                  setUserStatus("dnd");
                }}
              >
                {t("Do not Disturb")}
              </DropdownItem>
            </DropdownMenu>
          </Dropdown>
        </div>
        {/* End profile user */}

        {/* Start User profile description */}
        <SimpleBar
          style={{ maxHeight: "100%" }}
          className="p-4 user-profile-desc"
        >
          <div id="profile-setting-accordion" className="custom-accordion">
            <Card className="shadow-none border mb-2">
              <CustomCollapse
                title="Personal Info"
                isOpen={isOpen1}
                iconClass=""
                toggleCollapse={toggleCollapse1}
              >
                <div className="float-right">
                  <Button
                    onClick={() => setShowModalEditInfo(true)}
                    color="light"
                    size="sm"
                    type="button"
                  >
                    <i className="ri-edit-fill mr-1 align-middle"></i>{" "}
                    {t("Edit")}
                  </Button>
                </div>
                <div>
                  <p className="text-muted mb-1">{t("Name")}</p>
                  <h5 className="font-size-14">{user.name}</h5>
                </div>
                <div className="float-right">
                  <Button
                    onClick={() => setShowModalChangePassword(true)}
                    color="light"
                    size="sm"
                    type="button"
                  >
                    <i className="ri-edit-fill mr-1 align-middle"></i>{" "}
                    {t("Edit security")}
                  </Button>
                </div>
                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Email")}</p>
                  <h5 className="font-size-14">{user.email}</h5>
                </div>

                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Number")}</p>
                  <h5 className="font-size-14">{user.number}</h5>
                </div>
                {/* 
                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Time")}</p>
                  <h5 className="font-size-14">{t("11:40 AM")}</h5>
                </div>

                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Location")}</p>
                  <h5 className="font-size-14 mb-0">{t("California, USA")}</h5>
                </div> */}
              </CustomCollapse>
            </Card>
          </div>
          <div id="profile-setting-accordion" className="custom-accordion">
            <Card className="shadow-none border mb-2">
              <CustomCollapse
                title={t("Devices Settings")}
                isOpen={isOpen2}
                iconClass=""
                toggleCollapse={toggleCollapse2}
              >
                <Button
                  color="light"
                  size="sm"
                  type="button"
                  block
                  onClick={() => {
                    setShowModalDevicesConfig(true);
                  }}
                >
                  <i className="ri-edit-fill mr-1 align-middle"></i>{" "}
                  {t("Change devices settings")}
                </Button>
              </CustomCollapse>
            </Card>
          </div>
          <div id="profile-setting-accordion" className="custom-accordion">
            <Card className="shadow-none border mb-2">
              <CustomCollapse
                title={t("Telephone Extension Line Settings")}
                isOpen={isOpen3}
                iconClass=""
                toggleCollapse={toggleCollapse3}
              >
                <div className="mb-2 text-muted">{t("Follow me") + ":"}</div>

                <div>
                  <div
                    className={"mb-1 mt-3 text-muted"}
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      alignItems: "center",
                    }}
                  >
                    {t("On busy") + ":"}
                    <div id="TooltipExample1" className={"ml-1 mb-1"}>
                      <img
                        alt={"?"}
                        src={require("./../assets/images/doubt.png")}
                      />
                    </div>
                  </div>
                </div>
                <InputGroup className={"mb-2"}>
                  <InputGroupAddon addonType="prepend">
                    {lockedBusyInput === false ? (
                      <Button
                        style={{ backgroundColor: "#6a7fc0", border: "none" }}
                        onClick={() => {
                          setLockedBusyInput(!lockedBusyInput);
                          setBusy("");
                        }}
                      >
                        <div
                          style={{ fontWeight: "bold", color: "white" }}
                          className="ri-check-fill"
                        ></div>
                      </Button>
                    ) : (
                      <Button
                        style={{ backgroundColor: "#e1ecfb", border: "none" }}
                        onClick={() => setLockedBusyInput(!lockedBusyInput)}
                      >
                        <div
                          style={{ fontWeight: "bold", color: "black" }}
                          className="ri-close-line"
                        ></div>
                      </Button>
                    )}
                  </InputGroupAddon>
                  {lockedBusyInput ? (
                    <Input
                      value={busy}
                      placeholder={t("Destination")}
                      onClick={() => setLockedBusyInput(false)}
                      id="firstcheck"
                    />
                  ) : (
                    <Input
                      value={busy}
                      placeholder={t("Destination")}
                      onChange={(e) => setBusy(isExtensionLine(e.target.value))}
                      onFocus={() => setDisplay1(true)}
                      onBlur={() => setDisplay1(false)}
                      autoFocus
                    />
                  )}
                  {display1 &&
                    <div className={"mt-2"} style={{color: "red", fontSize: 12}}>
                      {t("Please enter only characters between 0-9, * and #.")}
                    </div>
                  }
                </InputGroup>

                <div>
                  <div
                    className={"mb-1 mt-3 text-muted"}
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      alignItems: "center",
                    }}
                  >
                    {t("On no answer") + ":"}
                    <div id="TooltipExample2" className={"ml-1 mb-1"}>
                      <img
                        alt={"?"}
                        src={require("./../assets/images/doubt.png")}
                      />
                    </div>
                  </div>
                </div>
                <InputGroup className={"mb-2"}>
                  <InputGroupAddon addonType="prepend">
                    {lockedNoAnswerInput === false ? (
                      <Button
                        style={{ backgroundColor: "#6a7fc0", border: "none" }}
                        onClick={() => {
                          setLockedNoAnswerInput(!lockedNoAnswerInput);
                          setNoAnswer("");
                        }}
                      >
                        <div
                          style={{ fontWeight: "bold", color: "white" }}
                          className="ri-check-fill"
                        ></div>
                      </Button>
                    ) : (
                      <Button
                        style={{ backgroundColor: "#e1ecfb", border: "none" }}
                        onClick={() =>
                          setLockedNoAnswerInput(!lockedNoAnswerInput)
                        }
                      >
                        <div
                          style={{ fontWeight: "bold", color: "black" }}
                          className="ri-close-line"
                        ></div>
                      </Button>
                    )}
                  </InputGroupAddon>
                  {lockedNoAnswerInput ? (
                    <Input
                      value={noAnswer}
                      placeholder={t("Destination")}
                      onClick={() => setLockedNoAnswerInput(false)}
                    />
                  ) : (
                    <Input
                      value={noAnswer}
                      placeholder={t("Destination")}
                      onChange={(e) => setNoAnswer(isExtensionLine(e.target.value))}
                      onFocus={() => setDisplay2(true)}
                      onBlur={() => setDisplay2(false)}
                    />
                  )}
                  {display2 &&
                    <div className={"mt-2"} style={{color: "red", fontSize: 12}}>
                      {t("Please enter only characters between 0-9, * and #.")}
                    </div>
                  }
                </InputGroup>

                <div>
                  <div
                    className={"mb-1 mt-3 text-muted"}
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      alignItems: "center",
                    }}
                  >
                    {t("When not registered") + ':'}
                    <div id="TooltipExample3" className={"ml-1 mb-1"}>
                      <img
                        alt={"?"}
                        src={require("./../assets/images/doubt.png")}
                      />
                    </div>
                  </div>
                </div>
                <InputGroup className={"mb-2"}>
                  <InputGroupAddon addonType="prepend">
                    {lockedNotRegisteredInput === false ? (
                      <Button
                        style={{ backgroundColor: "#6a7fc0", border: "none" }}
                        onClick={() => {
                          setLockedNotRegisteredInput(
                            !lockedNotRegisteredInput
                          );
                          setNotRegistered("");
                        }}
                      >
                        <div
                          style={{ fontWeight: "bold", color: "white" }}
                          className="ri-check-fill"
                        ></div>
                      </Button>
                    ) : (
                      <Button
                        style={{ backgroundColor: "#e1ecfb", border: "none" }}
                        onClick={() =>
                          setLockedNotRegisteredInput(!lockedNotRegisteredInput)
                        }
                      >
                        <div
                          style={{ fontWeight: "bold", color: "black" }}
                          className="ri-close-line"
                        ></div>
                      </Button>
                    )}
                  </InputGroupAddon>
                  {lockedNotRegisteredInput ? (
                    <Input
                      value={notRegistered}
                      placeholder={t("Destination")}
                      onClick={() => setLockedNotRegisteredInput(false)}
                    />
                  ) : (
                    <Input
                      value={notRegistered}
                      placeholder={t("Destination")}
                      onChange={(e) => setNotRegistered(isExtensionLine(e.target.value))}
                      onFocus={() => setDisplay3(true)}
                      onBlur={() => setDisplay3(false)}
                    />
                  )}
                  {display3 &&
                    <div className={"mt-2"} style={{color: "red", fontSize: 12}}>
                      {t("Please enter only characters between 0-9, * and #.")}
                    </div>
                  }
                </InputGroup>

                <div>
                  <div
                    className={"mb-1 mt-3 text-muted"}
                    style={{
                      display: "flex",
                      flexDirection: "row",
                      alignItems: "center",
                    }}
                  >
                    {t("Always") + ':'}
                    <div id="TooltipExample4" className={"ml-1 mb-1"}>
                      <img
                        alt={"?"}
                        src={require("./../assets/images/doubt.png")}
                      />
                    </div>
                  </div>
                </div>
                <InputGroup className={"mb-2"}>
                  <InputGroupAddon addonType="prepend">
                    {lockedFollowMeAlwaysInput === false ? (
                      <Button
                        style={{ backgroundColor: "#6a7fc0", border: "none" }}
                        onClick={() => {
                          setLockedFollowMeAlwaysInput(
                            !lockedFollowMeAlwaysInput
                          );
                          setFollowMeAlways("");
                        }}
                      >
                        <div
                          style={{ fontWeight: "bold", color: "white" }}
                          className="ri-check-fill"
                        ></div>
                      </Button>
                    ) : (
                      <Button
                        style={{ backgroundColor: "#e1ecfb", border: "none" }}
                        onClick={() =>
                          setLockedFollowMeAlwaysInput(
                            !lockedFollowMeAlwaysInput
                          )
                        }
                      >
                        <div
                          style={{ fontWeight: "bold", color: "black" }}
                          className="ri-close-line"
                        ></div>
                      </Button>
                    )}
                  </InputGroupAddon>
                  {lockedFollowMeAlwaysInput ? (
                    <Input
                      value={followMeAlways}
                      placeholder={t("Destination")}
                      onClick={() => setLockedFollowMeAlwaysInput(false)}
                    />
                  ) : (
                    <Input
                      value={followMeAlways}
                      placeholder={t("Destination")}
                      onChange={(e) => setFollowMeAlways(isExtensionLine(e.target.value))}
                      onFocus={() => setDisplay4(true)}
                      onBlur={() => setDisplay4(false)}
                    />
                  )}
                  {display4 &&
                    <div className={"mt-2"} style={{color: "red", fontSize: 12}}>
                      {t("Please enter only characters between 0-9, * and #.")}
                    </div>
                  }
                </InputGroup>

                <Tooltip
                  placement="right"
                  isOpen={tooltipOpen1}
                  target="TooltipExample1"
                  toggle={toggleTooltip1}
                >
                  {t(
                    "When the extension is busy the call will be forwarded to the extension indicated below"
                  )}
                </Tooltip>

                <Tooltip
                  placement="right"
                  isOpen={tooltipOpen2}
                  target="TooltipExample2"
                  toggle={toggleTooltip2}
                >
                  {t(
                    "When the extension does not answer the call it will be forwarded to the extension indicated below"
                  )}
                </Tooltip>

                <Tooltip
                  placement="right"
                  isOpen={tooltipOpen3}
                  target="TooltipExample3"
                  toggle={toggleTooltip3}
                >
                  {t(
                    "When the extension is not registered with the PABX the call it will be forwarded to the extension indicated below"
                  )}
                </Tooltip>

                <Tooltip
                  placement="right"
                  isOpen={tooltipOpen4}
                  target="TooltipExample4"
                  toggle={toggleTooltip4}
                >
                  {t("When enabling follow-me always, all calls will be forwarded to the indicated number")}
                </Tooltip>

                {isEveryDataEmpty() || isEveryInputLocked() ? (
                  <Button
                    color="light"
                    size="sm"
                    type="button"
                    block
                    disabled
                    className={"mt-4"}
                  >
                    {t("Save")}
                  </Button>
                ) : (
                  <Button
                    color="light"
                    size="sm"
                    type="button"
                    block
                    onClick={() => {
                      Swal.fire({
                        title:
                          "Agora, consulta-se a API para pegar o status atual de configuração!",
                        //Romero, para facilitar: os states utilizados foram: {busy, noAnswer, notRegistered, followMeAlways}
                        showClass: {
                          popup: "animate__animated animate__fadeInDown",
                        },
                        hideClass: {
                          popup: "animate__animated animate__fadeOutUp",
                        },
                      });
                      clearInputs();
                      lockAllInputs();
                    }}
                    className={"mt-4"}
                  >
                    {t("Save")}
                  </Button>
                )}
              </CustomCollapse>
            </Card>
          </div>
        </SimpleBar>
      </div>
      <ModalEditInfo />
      <ModalConfigDevices />
    </React.Fragment>
  );
}

export default SettingTab;
