import React, { createContext, useContext, useState } from "react";

interface ILayoutContextData {
  activeTab: string;
  setActiveTab(value: string): void;
  userSidebar: boolean;
  setUserSidebar(value: boolean): void;
  conversationName: string;
  setConversationName(value: string): void;
}

export const LayoutContext = createContext<ILayoutContextData>(
  {} as ILayoutContextData
);

export const LayoutProvider: React.FC = ({ children }) => {
  const [activeTab, setActiveTab] = useState("chat");
  const [userSidebar, setUserSidebar] = useState(false);
  const [conversationName, setConversationName] = useState("Doris Brown");

  return (
    <LayoutContext.Provider
      value={{
        activeTab,
        setActiveTab,
        userSidebar,
        setUserSidebar,
        conversationName,
        setConversationName,
      }}
    >
      {children}
    </LayoutContext.Provider>
  );
};

export function useLayout() {
  return useContext(LayoutContext);
}
