import React from "react";
import { useTranslation } from "react-i18next";
import { CardBody, Modal, ModalBody } from "reactstrap";
import { useLoading } from "../contexts/LoadingContext";
import logo from "./../assets/images/logo.png";
import { motion } from "framer-motion";
import { useAuth } from "../contexts/AuthContext";
import { IConnectionStatus } from "../alias/IUser";

const ModalDisconnected: React.FC = () => {
  const { t } = useTranslation();
  const { isDisconnected } = useLoading();
  const { user } = useAuth();

  return (
    <>
      {(isDisconnected ||
        user.connectionStatus === IConnectionStatus.DISCONNECTED) && (
        <Modal isOpen={isDisconnected} centered>
          <ModalBody>
            <CardBody>
              <div className="d-flex justify-content-center align-items center">
                <motion.img
                  src={logo}
                  alt="logo"
                  animate={{
                    rotate: 1440,
                  }}
                  transition={{
                    duration: 5,
                    repeat: Infinity,
                  }}
                  style={{ width: 30, height: 30 }}
                />
                <h5 className="ml-3 mt-1" style={{ userSelect: "none" }}>
                  {t("Reconnecting...")}
                </h5>
              </div>
            </CardBody>
          </ModalBody>
        </Modal>
      )}
    </>
  );
};

export default ModalDisconnected;
