import React from "react";
import { Input, UncontrolledTooltip, Label } from "reactstrap";
import { useAuth } from "../contexts/AuthContext";
import { useChat } from "../contexts/ChatContext";
import DeliverStatus from "../enuns/DeliverStatus";
import ReadStatus from "../enuns/ReadStatus";
import { useTranslation } from "react-i18next";
import Swal from 'sweetalert2'

const ChatInputAttachedFileButton: React.FC = () => {
  const { chats, setChats, activeChat } = useChat();
  const { user } = useAuth();

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let files = e.target.files;
    if (files?.length === 0) return;
    let file = files?.item(0);
    if (file && file.size > 15728640) {
      Swal.fire({
        icon: 'error',
        title: 'Oops...',
        text: 'File size must be less than 15mb!',
        footer: ''
      })
      return;
    }

    if (file) {
      onAddFile(file);
    }
  };

  const { t } = useTranslation();

  const onAddFile = (file: File) => {
    console.log(file);
    setChats(
      chats.map((cht) => {
        if (activeChat)
          if (cht.id === activeChat?.id) {
            cht.messages.push({
              id: `${cht.messages.length + 1}`,
              userName: user.name,
              message: "Carregando...",
              time: "",
              isImageMessage: false,
              isFileMessage: false,
              isVideoMessage: false,
              isAudioMessage: false,
              fileUrl: "",
              sendFile: file,
              toUser: activeChat.id,
              fromUser: user.id,
              deliverStatus: DeliverStatus.SENDING,
              readStatus: ReadStatus.UNREAD,
              deliverTimestamp: "",
              readTimestamp: "",
              content: "metadata",
              contentType: "type",
              isReplyMessage: false,
              isForwardMessage: false,
            });
            activeChat.attachedFileList?.push();
          }
        return cht;
      })
    );
  };

  return (
    <li className="list-inline-item input-file">
      <Label
        id="files"
        className="btn btn-link text-decoration-none font-size-16 btn-lg waves-effect"
      >
        <i className="ri-attachment-line"></i>
        <Input onChange={handleFileChange} type="file" name="fileInput" />
      </Label>
      <UncontrolledTooltip target="files" placement="top">
        {t("Attach File")}
      </UncontrolledTooltip>
    </li>
  );
};

export default ChatInputAttachedFileButton;
