import IContact from "../alias/IContact";
// ( + [ ) ? \ *

export const SearchContact = (contacts: IContact[], search: string) => {
  if(
    search.includes('@') || 
    search.includes('(') || 
    search.includes('[') || 
    search.includes('+') || 
    search.includes(')') || 
    search.includes('?') || 
    search.includes('/') || 
    search.includes('-') || 
    search.includes('*')
  ){
    return [];
  }else{
    return contacts.filter((item) => {
      return item.name.toUpperCase().match(search.toUpperCase());
    });
  }
};
