import React from 'react';
import { useChat } from '../contexts/ChatContext';

interface IMencionedMembers{
    message: string;
}

const MencionedMembers : React.FC<IMencionedMembers> = ({message}) => {
    const { activeChat } = useChat();
    const members = activeChat.members?.filter((item) => {return item.name.toUpperCase().match(message.split('@')[1])})
    return(
        <ul className="list-group" style={{ textAlign: "start", position: "absolute", bottom: 45 }}>
            {members?.map((mb) => {
                return(
                    <div className="list-group-item">{mb.name}</div>
                );
            })}
        </ul>
    );
}

export default MencionedMembers;
