import React, { useState } from "react";

import { Button, Card, Media, Badge } from "reactstrap";

//Simple bar
import SimpleBar from "simplebar-react";

//components
import CustomCollapse from "./CustomCollapse";

//i18n
import { useTranslation } from "react-i18next";

//context
import { useChat } from "../contexts/ChatContext";

import ChatType from "../enuns/ChatType";

import AttachedFilesTab from "./AttachedFilesTab";

interface IPros {
  show: boolean;
  setShow(value: boolean): void;
}

const ChatProfileSidebar: React.FC<IPros> = ({ show, setShow }) => {
  const { activeChat } = useChat();

  const [isOpen1, setIsOpen1] = useState(true);
  const [isOpen2, setIsOpen2] = useState(false);

  const { t } = useTranslation();

  const toggleCollapse1 = () => {
    setIsOpen1(!isOpen1);
    setIsOpen2(false);
  };

  const toggleCollapse3 = () => {
    setIsOpen2(!isOpen2);
    setIsOpen1(false);
  };

  const closeuserSidebar = () => {
    setShow(false);
  };

  return (
    <React.Fragment>
      <div
        style={{ display: show ? "block" : "none" }}
        className="user-profile-sidebar"
      >
        <div className="px-3 px-lg-4 pt-3 pt-lg-4">
          <div className="user-chat-nav text-right">
            <Button
              color="none"
              type="button"
              onClick={closeuserSidebar}
              className="nav-btn"
              id="user-profile-hide"
            >
              <i className="ri-close-line"></i>
            </Button>
          </div>
        </div>

        <div className="text-center p-4 border-bottom">
          <div className="mb-4 d-flex justify-content-center">
            {!activeChat.profilePicture ? (
              <div className="avatar-lg">
                <span className="avatar-title rounded-circle bg-soft-primary text-primary font-size-24">
                  {activeChat.name.trim().charAt(0)}
                </span>
              </div>
            ) : (
              <img
                src={activeChat.profilePicture}
                className="rounded-circle avatar-lg img-thumbnail"
                alt="chatvia"
              />
            )}
          </div>

          <h5 className="font-size-16 mb-1 text-truncate">{activeChat.name}</h5>
          <p className="text-muted text-truncate mb-1">
            {(() => {
              switch (activeChat.status) {
                case "online":
                  return (
                    <>
                      <i className="ri-record-circle-fill font-size-10 text-success mr-1"></i>
                    </>
                  );

                case "away":
                  return (
                    <>
                      <i className="ri-record-circle-fill font-size-10 text-warning mr-1"></i>
                    </>
                  );

                case "offline":
                  return (
                    <>
                      <i className="ri-record-circle-fill font-size-10 text-secondary mr-1"></i>
                    </>
                  );

                default:
                  return;
              }
            })()}
            Active
          </p>
        </div>
        {/* End profile user */}

        {/* Start user-profile-desc */}
        <SimpleBar
          style={{ maxHeight: "100%" }}
          className="p-4 user-profile-desc"
        >
          <div id="profile-user-accordion" className="custom-accordion">
            <Card className="shadow-none border mb-2">
              {/* import collaps */}
              <CustomCollapse
                title="About"
                iconClass="ri-user-2-line"
                isOpen={isOpen1}
                toggleCollapse={toggleCollapse1}
              >
                <div>
                  <p className="text-muted mb-1">{t("Name")}</p>
                  <h5 className="font-size-14">{activeChat.name}</h5>
                </div>

                <div className="text-muted">
                  <p className="mb-4">
                    {activeChat.userDescription == undefined ? "Olá! Eu estou usando o Squad!" : activeChat.userDescription}
                  </p>
                </div>

                <div className="mt-4">
                  <p className="text-muted mb-1">{t("Email")}</p>
                  <h5 className="font-size-14">{activeChat.email}</h5>
                </div>

                <div className="mt-4">
                  <p className="text-muted mb-1">{t("User sector")}</p>
                  <h5 className="font-size-14 mb-0">{activeChat.sector}</h5>
                </div>
              </CustomCollapse>
            </Card>

            <AttachedFilesTab list={activeChat.list}/>

            {activeChat.chatType === ChatType.GROUP && (
              <Card className="mb-1 shadow-none border">
                <CustomCollapse
                  title="Members"
                  iconClass="ri-group-line"
                  isOpen={isOpen2}
                  toggleCollapse={toggleCollapse3}
                >
                  <Card className="p-2 mb-2">
                    <Media className="align-items-center">
                      <div className="chat-user-img align-self-center mr-3">
                        <div className="avatar-xs">
                          <span className="avatar-title rounded-circle bg-soft-primary text-primary">
                            S
                          </span>
                        </div>
                      </div>
                      <Media body>
                        <div className="text-left">
                          <h5 className="font-size-14 mb-1">
                            {t("Sara Muller2")}
                            <Badge
                              color="danger"
                              className="badge-soft-danger float-right"
                            >
                              {t("Admin")}
                            </Badge>
                          </h5>
                          {/* <p className="text-muted font-size-13 mb-0">status</p> */}
                        </div>
                      </Media>
                    </Media>
                  </Card>
                </CustomCollapse>
              </Card>
            )}
          </div>
        </SimpleBar>
      </div>
    </React.Fragment>
  );
};

export default ChatProfileSidebar;
