import React, {useState} from "react";

import Lightbox from "react-image-lightbox";
import { Link } from "react-router-dom";


interface IProps {
  url: string;
}

const ImageMessage: React.FC<IProps> = ({ url }) => {
  
  const [isOpen, setIsOpen] = useState(false);
  const [currentImage, setCurrentImage] = useState("null");
  const toggleLightbox: any = (currentImage: any) => {
    setIsOpen(!isOpen);
    setCurrentImage(currentImage);
  };
  return <Link
  to="#"
  onClick={() => toggleLightbox(url)}
  className="popup-img d-inline-block m-1"
  title="title"
>
  <img alt="url" src={url} style={{maxWidth: "220px", maxHeight: "220px"}}/>
  {isOpen && (
    <Lightbox
      mainSrc={currentImage}
      onCloseRequest={() => setIsOpen(!isOpen)}
      imageTitle="Image File"
    />
  )}
</Link>
};

export default ImageMessage;
