import React from "react";
import { RiCloseFill } from "react-icons/ri";
import { useParticipantsDrawer } from "../contexts/ParticipantsDrawerContext";

import ParticipantItem from "./ParticipantItem";

const ParticipantsDrawer: React.FC = () => {
  const { participants, show, setShow, description } = useParticipantsDrawer();

  if (!show) {
    return <></>;
  }

  return (
    <aside className="participants-container">
      <div className="participants-container-header">
        <div>{description}</div>
        <span>Participantes</span>
        <RiCloseFill onClick={() => setShow(false)} />
      </div>
      <div className="participants-container-body">
        {participants?.map((participant, i) => (
          <ParticipantItem participant={participant} key={i} />
        ))}
      </div>
    </aside>
  );
};

export default ParticipantsDrawer;
