import React from "react";
import {
  DropdownMenu,
  DropdownItem,
  DropdownToggle,
  UncontrolledDropdown,
} from "reactstrap";
import IChat from "../alias/IChat";
import IMessage from "../alias/IMessage";
import { useTranslation } from "react-i18next";
import { useModal } from "../contexts/ModalContext";
import useCopy from "@react-hook/copy";
import { useChat } from "../contexts/ChatContext";
import ChatType from "../enuns/ChatType";

interface IProps {
  activeChat: IChat;
  message: IMessage;
}


const ChatMessageMenuOption: React.FC<IProps> = ({ activeChat, message }) => {
  const { t } = useTranslation();
  const { setShowModalForwardMessageTo } = useModal();
  const { setReplyMsg, setFavoriteMessagePayload, setDeleteFavoriteMessage} = useChat();
  const { copy } = useCopy(message.message);

  if (!activeChat.isTyping) {
    return (
      <UncontrolledDropdown className="align-self-start">
        <DropdownToggle tag="a">
          <i className="ri-more-2-fill"></i>
        </DropdownToggle>
        <DropdownMenu>
          <DropdownItem onClick={copy}>
            {t("Copy")}
            <i className="ri-file-copy-line float-right text-muted"></i>
          </DropdownItem>
          {/* <DropdownItem>
            {t("Save")} <i className="ri-save-line float-right text-muted"></i>
          </DropdownItem> */}
          <DropdownItem
            onClick={() => {
              setReplyMsg(message);
            }}
          >
            {t("Reply")}{" "}
            <i className="ri-reply-line float-right text-muted"></i>
          </DropdownItem> 
           <DropdownItem
            onClick={() => {
              setFavoriteMessagePayload({
                messageId: message.id,
                contactTo: activeChat.jid,
                contactType: activeChat.chatType === ChatType.USER ? "contact" : "group",
                message: message.message,
                messageDate: message.time.split("T").join(" ").slice(0, 19),
                content: message.message,
                contentType: "message",
              });
            }}
          >
            {t("Favorite")}{" "}
            <i className="ri-star-line float-right text-muted"></i>
          </DropdownItem>
          <DropdownItem
            onClick={() => {
              setDeleteFavoriteMessage({messageId: message.id, contactType: activeChat.chatType === ChatType.USER ? "contact" : "group", id: message.id });
            }}
          >
            {t("Unfavorite")}{" "}
            <i className="ri-star-line float-right text-muted"></i>
          </DropdownItem>
          <DropdownItem onClick={() => setShowModalForwardMessageTo(message)}>
            {t("Forward")}{" "}
            <i className="ri-group-line float-right text-muted" />
          </DropdownItem>
          {/* <DropdownItem onClick={() => deleteMessage(message.id)}>
            Delete <i className="ri-delete-bin-line float-right text-muted"></i>
          </DropdownItem> */}
        </DropdownMenu>
      </UncontrolledDropdown>
    );
  }

  return <></>;
};

export default ChatMessageMenuOption;
