import React, { useState } from "react";
import {
  TabContent,
  TabPane,
  Nav,
  NavItem,
  NavLink,
  Row,
  Col,
} from "reactstrap";
import classnames from "classnames";

import MediaTabFiles from "./MediaTabFiles";
import DocumentTabFiles from "./DocumentTabFiles";
import IFiles from "../alias/IFiles";

interface IAttachedFilesTab{
  list: IFiles[] | undefined;
}

const AttachedFilesTab: React.FC<IAttachedFilesTab> = ({ list }) => {
  const [activeTab, setActiveTab] = useState("1");
  return (
    <div>
      <Nav tabs>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === "1" })}
            onClick={() => {
              setActiveTab("1");
            }}
          >
            Media
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === "2" })}
            onClick={() => {
              setActiveTab("2");
            }}
          >
            Documents
          </NavLink>
        </NavItem>
      </Nav>
      <TabContent activeTab={activeTab}>
        <TabPane tabId="1">
          <Row>
            <Col sm="12">
              <MediaTabFiles />
            </Col>
          </Row>
        </TabPane>
        <TabPane tabId="2">
          <DocumentTabFiles />
        </TabPane>
      </TabContent>
    </div>
  );
};

export default AttachedFilesTab;
