import React from "react";
import IMessage from "../alias/IMessage";
import { DateTime } from "luxon";
import { useTranslation } from "react-i18next";

interface IProps {
  message: IMessage;
  messages: IMessage[];
  i: number;
}

const ChatMessageDivisionData: React.FC<IProps> = ({
  message,
  messages,
  i,
}) => {
  const { t } = useTranslation();

  if (i > 0) {
    if (
      DateTime.fromISO(message.time).day ===
      DateTime.fromISO(messages[i - 1].time).day
    ) {
      return <></>;
    }
  }

  const printDate = () => {
    let date = DateTime.fromISO(message.time);
    let today = DateTime.local();

    if (
      date.toLocaleString(DateTime.DATE_SHORT) ===
      today.toLocaleString(DateTime.DATE_SHORT)
    ) {
      return t("today");
    } else {
      return date.toLocaleString(DateTime.DATE_SHORT);
    }
  };

  return (
    <li>
      <div className="chat-day-title">
        <span className="title">{printDate()}</span>
      </div>
    </li>
  );
};

export default ChatMessageDivisionData;
