import React from 'react';
import { useTranslation } from 'react-i18next';
import { Card, Media, Badge } from 'reactstrap';
import ReactImageFallback from "react-image-fallback";
import { Link } from "react-router-dom";
import { useContact } from '../contexts/ContactContext';
import { useAuth } from '../contexts/AuthContext';
import { useChat } from '../contexts/ChatContext';
import { useGroup } from '../contexts/GroupContext';
import Swal from 'sweetalert2'
import IChat from '../alias/IChat';
import IMember from '../alias/IMember';
import { ConferenceRoomParticipantType } from '../services/business/Conferences';

interface IProps{
    member: IMember;
    defaultAvatar: any;
    isAdmin: any;
}

const ChangeGroupNameButton : React.FC<IProps> = ({ isAdmin, defaultAvatar, member, }) => {
    const { activeChat, chats, openChat, setShowChat } = useChat();
    const { user } = useAuth();
    const moderators = activeChat
    .conferenceRoomParticipants?.filter(
      (participant) => 
          participant.userModerator === true && 
          participant.type === ConferenceRoomParticipantType.MODERATOR &&
          participant.userUuid === user.id
    );
    const isModerator = (member: IMember) => {
      moderators?.find((moderator) => moderator.userUuid === member.userId)
    }
    const { t } = useTranslation();
    const { setClickedContact, contacts, contactsOutsideUserContacts, openContact } = useContact();
    const { setToggleMemberRole, setRemovedMember, setGroupToRemoveContact } = useGroup();
    const openChatAction = (chat: IChat) => {
        openChat(chat);
        setShowChat(true);
    };

    return <Card key={member.userId} className="p-2 mb-2">
    <Media className="align-items-center">
      <ReactImageFallback
        src={member.profilePicture}
        className="rounded-circle avatar-xs mr-2"
        alt="chatvia"
        fallbackImage={defaultAvatar}
      />
      <Media body>
        <div className="text-left">
          <h5 className="font-size-14 mb-1">
            <Link
              to="#"
              onClick={() => {
                chats.forEach((cht: any) => {
                  if (cht.id === member.userId) {
                    openChatAction(cht);
                    contacts.forEach((ctc: any) => {
                      if (ctc.id === member.userId) {
                        setClickedContact(ctc);
                      }
                    });
                  } else {
                    contactsOutsideUserContacts.forEach(
                      (ctcOut) => {
                        if (ctcOut.id === member.userId) {
                          openContact(ctcOut);
                          setClickedContact(ctcOut);
                        }
                      }
                    );
                  }
                });
              }}
            >
              {member.name}
            </Link>

            {((isAdmin && user.id !== member.userId) || activeChat.conferenceRoomParticipants) && (
              <span
                className="float-right ml-2"
                style={{ cursor: "pointer" }}
                onClick={() =>
                  Swal.fire({
                    title:
                      t("Remove") + " " + member.name + "?",
                    text: "",
                    icon: "warning",
                    showCancelButton: true,
                    confirmButtonColor: "#4e5e96",
                    cancelButtonColor: "#d33",
                    cancelButtonText: t("No"),
                    confirmButtonText: t("Yes"),
                    customClass: {
                      popup: "swal-mini-popup"
                    }
                  }
                  ).then((result) => {
                    if (result.isConfirmed) {
                      activeChat.conferenceRoomParticipants ?
                      Swal.fire({
                        position: 'center',
                        icon: 'success',
                        title: member.name + " " + t("has been removed!"),
                        showConfirmButton: false,
                        timer: 1500,
                        customClass: {
                          popup: "swal-mini-popup"
                        }
                      })
                        : 
                      Swal.fire({
                        position: 'center',
                        icon: 'success',
                        title: member.name + " " + t("has been removed!"),
                        showConfirmButton: false,
                        timer: 1500,
                        customClass: {
                          popup: "swal-mini-popup"
                        }
                      })
                      setRemovedMember(member);
                      setGroupToRemoveContact(
                        activeChat.groupId
                      );
                    }
                  })
                }
              >
                <i className="ri-close-fill"></i>
              </span>
            )}
            {member.role === "admin" || isModerator ?
              (
              <Badge
                color="danger"
                className="badge-soft-danger float-right"
                onClick={() =>
                  isAdmin &&
                  Swal.fire({
                    title:
                      t("Turn") +
                      " " +
                      member.name +
                      " " +
                      t("a member?"),
                    text: "",
                    icon: "warning",
                    showCancelButton: true,
                    confirmButtonColor: "#4e5e96",
                    cancelButtonColor: "#d33",
                    cancelButtonText: t("No"),
                    confirmButtonText: t("Yes"),
                    customClass: {
                      popup: "swal-mini-popup"
                    }
                  }).then((result) => {
                    if (result.isConfirmed) {
                      Swal.fire({
                        position: 'center',
                        icon: 'success',
                        title: member.name + " " + t("is now a member!"),
                        showConfirmButton: false,
                        timer: 1500,
                        customClass: {
                          popup: "swal-mini-popup"
                        }
                      })
                    }
                    setToggleMemberRole({
                      mbId: member.userId,
                      gpId: activeChat.groupId
                        ? activeChat.groupId
                        : "",
                      mbRole: member.role,
                      mb: member,
                    });
                  })
                }
                style={{ cursor: "pointer" }}
              >
                {t("Admin")}
              </Badge>
            ) : (
              <Badge
                color="success"
                className="badge-soft-success float-right"
                style={{ cursor: "pointer" }}
                onClick={() =>
                  isAdmin &&
                  Swal.fire({
                    title:
                      t("Turn") +
                      " " +
                      member.name +
                      " " +
                      t("an administrator?"),
                    text: "",
                    icon: "warning",
                    showCancelButton: true,
                    confirmButtonColor: "#4e5e96",
                    cancelButtonColor: "#d33",
                    cancelButtonText: t("No"),
                    confirmButtonText: t("Yes"),
                    customClass: {
                      popup: "swal-mini-popup"
                    }
                  }).then((result) => {
                    if (result.isConfirmed) {
                      Swal.fire({
                        position: 'center',
                        icon: 'success',
                        title: member.name + " " + t("is now an administrator!"),
                        showConfirmButton: false,
                        timer: 1500,
                        customClass: {
                          popup: "swal-mini-popup"
                        }
                      })
                      setToggleMemberRole({
                        mbId: member.userId,
                        gpId: activeChat.groupId
                          ? activeChat.groupId
                          : "",
                        mbRole: member.role,
                        mb: member,
                      });
                    }
                  })
                }
              >
                {t("Member")}
              </Badge>
            )}
          </h5>
        </div>
      </Media>
    </Media>
  </Card>
}

export default ChangeGroupNameButton;
